import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

import logoNuevo from "../assets/img/logonuevo.png";

export default function Home() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <header className="TopBar TopBar--home">
        <div className="TopBar-logo">
          <span className="TopBar-title">Rustaco</span>
        </div>

        <nav className="TopBar-links">
          <Link to="/equipos" className="nav-btn">Equipos</Link>
          <Link to="/reglas" className="nav-btn">Reglas</Link>
          <Link to="/applys" className="nav-btn">Applys</Link>
          <Link to="/about" className="nav-btn">About</Link>
        </nav>
      </header>

      <main className="home-hero">
        <div className="panel home-hero-panel">
          <img className="home-hero-logo" src={logoNuevo} alt="Rustaco" />

          <p className="regla-body" style={{ textAlign: "center", marginTop: 10 }}>
            El video se controla desde public/index.html.
          </p>

          <div style={{ display: "flex", gap: 10, flexWrap: "wrap", justifyContent: "center", marginTop: 14 }}>
            <Link to="/equipos" className="btn-primary">Equipos</Link>
            <Link to="/reglas" className="btn-primary">Reglas</Link>
          </div>
        </div>
      </main>
    </div>
  );
}
