import React from "react";
import "../assets/home-dark.css";

const flagChile = "https://flagcdn.com/w20/cl.png";
const flagUSA = "https://flagcdn.com/w20/us.png";
const flagBrazil = "https://flagcdn.com/w20/br.png";

/**
 * TODO IMPORTANTE:
 * Pega aquí el contenido REAL de RULES_BY_LANG desde:
 *   c:\Users\tebym\Desktop\rustacoweb\reglasold.jsx
 * (no se puede importar desde fuera de /src con react-scripts)
 */
const RULES_BY_LANG = {
  es: [
    {
      title: "Formato general",
      items: ["128 jugadores", "Equipos de 8", "Eventos especiales durante el evento"],
    },
    {
      title: "Comunicación oficial",
      items: ["La comunicación oficial es por Discord"],
    },
    {
      title: "Toxicidad",
      items: ["Prohibido racismo/discriminación"],
    },
  ],
  en: [
    {
      title: "General format",
      items: ["128 players", "Teams of 8", "Special events during the event"],
    },
    {
      title: "Official communication",
      items: ["Official communication is via Discord"],
    },
    {
      title: "Toxicity",
      items: ["No racism/discrimination"],
    },
  ],
  pt: [
    {
      title: "Formato geral",
      items: ["128 jogadores", "Equipes de 8", "Eventos especiais durante o evento"],
    },
    {
      title: "Comunicação oficial",
      items: ["Comunicação oficial via Discord"],
    },
    {
      title: "Toxicidade",
      items: ["Proibido racismo/discriminação"],
    },
  ],
};

function getTitle(lang) {
  if (lang === "en") return "Rustaco II Rules";
  if (lang === "pt") return "Regras Rustaco II";
  return "Reglamento Rustaco II";
}

export default function Reglas() {
  const [lang, setLang] = React.useState("es");

  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  const rules = RULES_BY_LANG[lang] || RULES_BY_LANG.es;

  return (
    <div className="home-dark has-video-bg">
      <header className="TopBar TopBar--home">
        <nav className="TopBar-links TopBar-links--left" aria-label="Navegación">
          <a href="/reglas" className="nav-btn">Rules</a>
          <a href="/equipos" className="nav-btn">Teams</a>
          <a href="/events" className="nav-btn">Stats</a>
        </nav>
      </header>

      <main style={{ position: "relative", zIndex: 20, padding: "2.6rem 1rem 3rem" }}>
        <div className="panel" style={{ marginTop: 0, maxWidth: 1050 }}>
          <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", gap: 12, marginBottom: 16 }}>
            <h1 className="reglas-title" style={{ margin: 0 }}>{getTitle(lang)}</h1>

            <div className="hud-flags" aria-label="Language">
              <button className={`icon-btn flag ${lang === "es" ? "lang-btn-active" : ""}`} onClick={() => setLang("es")} title="Español">
                <img src={flagChile} alt="ES" />
              </button>
              <button className={`icon-btn flag ${lang === "en" ? "lang-btn-active" : ""}`} onClick={() => setLang("en")} title="English">
                <img src={flagUSA} alt="EN" />
              </button>
              <button className={`icon-btn flag ${lang === "pt" ? "lang-btn-active" : ""}`} onClick={() => setLang("pt")} title="Português">
                <img src={flagBrazil} alt="PT" />
              </button>
            </div>
          </div>

          <div className="reglas-content">
            {rules.map((sec, idx) => (
              <div key={idx} className="regla-card">
                <div className="regla-header">
                  <span className="regla-header-dot" aria-hidden />
                  <div className="regla-header-title">{sec.title}</div>
                </div>

                <div className="regla-body">
                  {!!(sec.paragraphs && sec.paragraphs.length) && (
                    <div>
                      {sec.paragraphs.map((p, i) => (
                        <p key={i} style={{ margin: "0 0 10px 0" }}>{p}</p>
                      ))}
                    </div>
                  )}

                  {!!(sec.items && sec.items.length) && (
                    <ul>
                      {sec.items.map((line, i) => (
                        <li key={i}>{line}</li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>
            ))}
          </div>

          <div className="reglas-cta-row">
            <a href="/" className="nav-btn" style={{ background: "rgba(255,255,255,0.06)", border: "1px solid rgba(255,255,255,0.12)" }}>
              Volver
            </a>
          </div>
        </div>
      </main>
    </div>
  );
}
