import React from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

// Fondo animado (igual que Home.jsx)
import fondo from "../assets/img/fondo.jpg";

// Animación de fade-in
const fadeVariants = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.7, ease: [0.39, 0.575, 0.565, 1] } }
};

const teams = Array.from({ length: 16 }, (_, i) => ({
  name: `Team ${i + 1}`,
  status: "A confirmar"
}));

const AnimatedBackground = () => (
  <div
    style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      zIndex: 0,
      pointerEvents: 'none',
      overflow: 'hidden'
    }}
  >
    <div
      style={{
        position: 'absolute',
        width: '100%',
        height: '100%',
        backgroundImage: `url(${fondo})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        filter: 'blur(8px) brightness(0.45) grayscale(0.1)',
        opacity: 0.7,
        zIndex: 1
      }}
    />
    <svg
      width="100vw"
      height="100vh"
      style={{
        position: 'absolute',
        top: 0,
        left: 0,
        zIndex: 2,
        pointerEvents: 'none'
      }}
    >
      {[...Array(18)].map((_, i) => (
        <circle
          key={i}
          cx={Math.random() * window.innerWidth}
          cy={Math.random() * window.innerHeight}
          r={18 + Math.random() * 22}
          fill={i % 3 === 0 ? "#e25822" : i % 2 === 0 ? "#7289da" : "#27ae60"}
          opacity={0.13 + Math.random() * 0.09}
        >
          <animate
            attributeName="cy"
            values={`0;${window.innerHeight};0`}
            dur={`${6 + Math.random() * 6}s`}
            repeatCount="indefinite"
          />
          <animate
            attributeName="cx"
            values={`${Math.random() * window.innerWidth};${Math.random() * window.innerWidth};${Math.random() * window.innerWidth}`}
            dur={`${8 + Math.random() * 8}s`}
            repeatCount="indefinite"
          />
        </circle>
      ))}
    </svg>
  </div>
);

const Equipos = () => (
  <div style={{ minHeight: "100vh", position: "relative", zIndex: 2 }}>
    <AnimatedBackground />
    {/* Botón volver al inicio */}
    <div style={{
      position: "fixed",
      top: 32,
      right: 40,
      zIndex: 10
    }}>
      <Link to="/" style={{
        background: "linear-gradient(90deg, #27ae60 60%, #e25822 100%)",
        color: "#fff",
        fontWeight: 700,
        fontSize: "1.08rem",
        padding: "0.7rem 1.7rem",
        border: "none",
        borderRadius: 12,
        boxShadow: "0 2px 8px #0007",
        cursor: "pointer",
        letterSpacing: "1px",
        textDecoration: "none",
        transition: "background 0.2s, transform 0.2s"
      }}>
        Volver al inicio
      </Link>
    </div>
    <motion.div
      initial="hidden"
      animate="visible"
      variants={fadeVariants}
      style={{
        padding: "3rem 0 2rem 0",
        position: "relative",
        zIndex: 3,
        maxWidth: 1200,
        margin: "0 auto"
      }}
    >
      <h1 style={{
        color: "#fff",
        fontWeight: 900,
        fontSize: "2.3rem",
        letterSpacing: "2px",
        textAlign: "center",
        marginBottom: "2.5rem",
        textShadow: "2px 2px 8px #000a"
      }}>
        Equipos del Torneo
      </h1>
      <motion.div
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.2 }}
        variants={fadeVariants}
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(4, 1fr)",
          gap: "2rem",
          maxWidth: 1000,
          margin: "0 auto"
        }}
      >
        {teams.map((team, idx) => (
          <motion.div
            key={idx}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, amount: 0.2 }}
            transition={{ duration: 0.5, delay: idx * 0.04 }}
            style={{
              background: "linear-gradient(135deg, #23201a 70%, #3a4bd8 100%)",
              borderRadius: "16px",
              boxShadow: "0 2px 12px #000a, 0 0 0 1.5px #7289da88",
              padding: "2.2rem 1.2rem",
              minHeight: 120,
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              justifyContent: "center",
              gap: "0.7rem",
              border: "none",
              fontFamily: "Montserrat, Arial, sans-serif",
              fontWeight: 700,
              fontSize: "1.15rem",
              color: "#fff",
              letterSpacing: "1px",
              position: "relative",
              transition: "transform 0.18s cubic-bezier(.4,0,.2,1), box-shadow 0.18s cubic-bezier(.4,0,.2,1)",
              overflow: "hidden"
            }}
          >
            <span style={{
              fontSize: "1.3rem",
              color: "#b3cfff",
              fontWeight: 900,
              textShadow: "1px 1px 8px #23272a"
            }}>
              {team.name}
            </span>
            <span style={{
              fontSize: "1.08rem",
              color: "#fff",
              fontWeight: 600,
              opacity: 0.85,
              letterSpacing: "1px",
              marginTop: "0.2rem"
            }}>
              {team.status}
            </span>
          </motion.div>
        ))}
      </motion.div>
    </motion.div>
    {/* --- CSS RESPONSIVO --- */}
    <style>
      {`
        @media (max-width: 900px) {
          h1 {
            font-size: 1.5rem !important;
          }
          div[style*="grid-template-columns"] {
            grid-template-columns: repeat(2, 1fr) !important;
            gap: 1rem !important;
          }
        }
        @media (max-width: 600px) {
          div[style*="grid-template-columns"] {
            grid-template-columns: 1fr !important;
            gap: 0.7rem !important;
          }
          .equipos-card {
            padding: 1.1rem 0.5rem !important;
            font-size: 0.97rem !important;
          }
        }
      `}
    </style>
    {/* --- FIN CSS RESPONSIVO --- */}
  </div>
);

export default Equipos;
