import React from "react";

const teams = Array.from({ length: 16 }, (_, i) => ({
  name: `Team ${i + 1}`,
  status: "A confirmar"
}));

const Equipos = () => (
  <div style={{ padding: 24 }}>
    <h1>Equipos del Torneo</h1>
    <div style={{
      display: "grid",
      gridTemplateColumns: "repeat(4, 1fr)",
      gap: "1.5rem",
      maxWidth: 900,
      margin: "0 auto"
    }}>
      {teams.map((team, idx) => (
        <div key={idx} style={{
          border: "1.5px solid #e25822",
          borderRadius: 12,
          padding: "1.5rem 1rem",
          background: "#23201a",
          color: "#fff",
          textAlign: "center",
          fontWeight: 700,
          fontSize: "1.15rem",
          boxShadow: "0 2px 12px #000a"
        }}>
          <div style={{ fontSize: "1.3rem", color: "#e25822", fontWeight: 900, marginBottom: 8 }}>
            {team.name}
          </div>
          <div style={{ fontSize: "1.08rem", color: "#fff", opacity: 0.85 }}>
            {team.status}
          </div>
        </div>
      ))}
    </div>
  </div>
);

export default Equipos;
