import React, { useEffect, useState } from 'react';

const ADMIN_STEAM_ID = '76561198000000000'; // Usa el mismo SteamID que en backend y Home.jsx

const Admin = () => {
  const [user, setUser] = useState(null);
  const [checking, setChecking] = useState(true);

  useEffect(() => {
    fetch('http://192.168.1.178:3001/api/user', { credentials: 'include' })
      .then(res => res.json())
      .then(data => {
        setUser(data && data.steamid ? data : null);
        setChecking(false);
      })
      .catch(() => setChecking(false));
  }, []);

  if (checking) {
    return <div style={{ color: '#fff', textAlign: 'center', margin: '2rem' }}>Cargando...</div>;
  }

  if (!user || user.steamid !== ADMIN_STEAM_ID) {
    return (
      <div style={{
        maxWidth: 600,
        margin: '4rem auto',
        background: 'rgba(24,24,24,0.97)',
        borderRadius: 24,
        boxShadow: '0 8px 32px #000b',
        padding: '2.5rem 2rem',
        textAlign: 'center',
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif'
      }}>
        <h2 style={{ color: '#e25822', fontWeight: 900, fontSize: '2rem', marginBottom: '1.5rem' }}>
          Acceso denegado
        </h2>
        <p>No tienes permisos para ver este panel.</p>
      </div>
    );
  }

  return (
    <div style={{
      maxWidth: 700,
      margin: '4rem auto',
      background: 'rgba(24,24,24,0.97)',
      borderRadius: 24,
      boxShadow: '0 8px 32px #000b',
      padding: '2.5rem 2rem',
      textAlign: 'center',
      color: '#fff',
      fontFamily: 'Montserrat, Arial, sans-serif'
    }}>
      <h1 style={{
        color: '#27ae60',
        fontWeight: 900,
        fontSize: '2.2rem',
        marginBottom: '1.5rem',
        letterSpacing: '2px',
        textShadow: '0 2px 18px #000a'
      }}>
        Admin Panel
      </h1>
      <div style={{
        background: 'linear-gradient(90deg, #23201a 70%, #27ae60 100%)',
        borderRadius: 14,
        boxShadow: '0 2px 12px #27ae6088',
        padding: '1.2rem 1rem',
        marginBottom: '1.5rem',
        fontSize: '1.18rem',
        fontWeight: 600,
        color: '#fff',
        letterSpacing: '1px',
        textShadow: '0 1px 8px #000a',
        border: '1.5px solid #27ae60cc'
      }}>
        Bienvenido, {user.name} <br />
        Aquí puedes gestionar el evento y ver información exclusiva de administración.
      </div>
      {/* Aquí puedes agregar más herramientas de admin */}
      <span style={{
        color: '#b3cfff',
        fontSize: '1.05rem',
        fontWeight: 500,
        display: 'block',
        marginTop: '1.2rem',
        opacity: 0.85
      }}>
        (Solo visible para el usuario administrador)
      </span>
    </div>
  );
};

export default Admin;
