import React, { useRef, useEffect } from 'react';
import logo from '../assets/img/logorustaco.png';
import rustpmc1 from '../assets/img/rustpmc1.png';
import rustpmc2 from '../assets/img/rustpmc2.png';
import rustpmc3 from '../assets/img/rustpmc3.png';
import rustpmc4 from '../assets/img/rustpmc4.png';
import '../assets/styles.css';

// Hook para animaciones on-scroll
function useRevealOnScroll(ref, options = {}) {
  useEffect(() => {
    const node = ref.current;
    if (!node) return;
    const handleIntersect = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          node.classList.add('reveal-visible');
        } else {
          node.classList.remove('reveal-visible');
        }
      });
    };
    const observer = new window.IntersectionObserver(handleIntersect, {
      threshold: options.threshold || 0.15
    });
    observer.observe(node);
    return () => observer.disconnect();
  }, [ref, options.threshold]);
}

const TopBar = ({ onFormatoClick, onInfoClick }) => (
  <div
    style={{
      width: '100%',
      minHeight: '80px',
      background: 'linear-gradient(90deg, #181818 60%, #e25822 120%)',
      boxShadow: '0 2px 12px #0008',
      display: 'flex',
      alignItems: 'center',
      padding: '0 4rem',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      gap: '2rem'
    }}
  >
    <div style={{
      display: 'flex',
      alignItems: 'center',
      gap: '2.5rem',
      minWidth: 0,
      marginLeft: '2rem'
    }}>
      <img
        src={logo}
        alt="Rustaco Logo"
        style={{
          borderRadius: '50%',
          width: 64,
          height: 64,
          objectFit: 'cover',
          boxShadow: '0 1px 12px #0007'
        }}
      />
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '2rem',
        marginLeft: '2rem'
      }}>
        <button className="nav-btn" onClick={onFormatoClick}>Formato</button>
        <button className="nav-btn" onClick={onInfoClick}>Sobre Rustaco</button>
        <a href="/events" className="nav-btn">Stats</a>
      </div>
    </div>
  </div>
);

const Header = () => {
  const headerRef = useRef(null);
  useRevealOnScroll(headerRef, { threshold: 0.2 });

  return (
    <header
      ref={headerRef}
      className="header reveal"
      style={{
        marginTop: '0',
        padding: '3rem 0 1.2rem 0',
        textAlign: 'center',
        minHeight: '30vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center'
      }}
    >
      <div className="logo-glow-container">
        <img
          src={logo}
          alt="Rustaco Logo"
          style={{
            width: 320,
            height: 320,
            borderRadius: '50%',
            objectFit: 'cover',
            boxShadow: '0 2px 18px #000a',
            margin: '0 auto',
            marginBottom: '1.5rem',
            position: 'relative',
            zIndex: 2
          }}
        />
        <div className="logo-glow-bg"></div>
      </div>
    </header>
  );
};

const FormatoSection = React.forwardRef((props, ref) => {
  const sectionRef = useRef(null);
  const titleRef = useRef(null);
  const listRef = useRef(null);
  const imgRef = useRef(null);

  useRevealOnScroll(sectionRef, { threshold: 0.15 });
  useRevealOnScroll(titleRef, { threshold: 0.15 });
  useRevealOnScroll(listRef, { threshold: 0.15 });
  useRevealOnScroll(imgRef, { threshold: 0.15 });

  return (
    <section
      ref={node => {
        sectionRef.current = node;
        if (typeof ref === 'function') ref(node);
        else if (ref) ref.current = node;
      }}
      className="reveal"
      style={{
        maxWidth: 1600,
        minHeight: '60vh',
        margin: '0 auto',
        marginTop: '26rem',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '0 2rem',
        textAlign: 'left',
        gap: '0.5rem',
        boxSizing: 'border-box',
        width: '100%'
      }}
    >
      <div
        style={{
          flex: 1.2,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'flex-end',
          justifyContent: 'center',
          minWidth: 0
        }}
      >
        <div ref={titleRef} className="reveal">
          <h2
            style={{
              fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
              fontWeight: 800,
              color: 'var(--rust-orange)',
              marginBottom: '2rem',
              fontSize: '2.5rem',
              letterSpacing: '1px'
            }}
          >
            Formato - Rustaco II
          </h2>
        </div>
        <div ref={listRef} className="reveal">
          <ul style={{
            color: '#f5f5f5',
            fontFamily: 'Montserrat, Arial, sans-serif',
            fontWeight: 500,
            fontSize: '1.5rem',
            margin: 0,
            maxWidth: 650,
            paddingLeft: '1.2em',
            listStyle: 'disc'
          }}>
            <li>
              Rustaco II contará con <b>96 streamers</b> participando en el evento.
            </li>
            <li>
              12 equipos formados por 8 jugadores lucharán por el primer puesto.
            </li>
            <li>
              Cada equipo tendrá su propia isla donde podrán construir y equiparse estratégicamente para el enfrentamiento final.
            </li>
          </ul>
        </div>
      </div>
      <div
        style={{
          flex: 1,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minWidth: 0,
          position: 'relative'
        }}
      >
        <div ref={imgRef} className="logo-glow-container reveal">
          <img
            src={rustpmc4}
            alt="Evento Rust"
            style={{
              width: '100%',
              maxWidth: '1000px',
              minWidth: '400px',
              borderRadius: 0,
              boxShadow: 'none',
              objectFit: 'contain',
              marginLeft: '0.5rem',
              marginRight: 0,
              background: 'none',
              animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)',
              display: 'block',
              position: 'relative',
              zIndex: 2
            }}
          />
          <div className="logo-glow-bg"></div>
        </div>
      </div>
    </section>
  );
});

const AboutSection = React.forwardRef((props, ref) => {
  const sectionRef = useRef(null);
  const titleRef = useRef(null);
  const textRef = useRef(null);
  const imgRef = useRef(null);

  useRevealOnScroll(sectionRef, { threshold: 0.15 });
  useRevealOnScroll(titleRef, { threshold: 0.15 });
  useRevealOnScroll(textRef, { threshold: 0.15 });
  useRevealOnScroll(imgRef, { threshold: 0.15 });

  return (
    <section
      ref={node => {
        sectionRef.current = node;
        if (typeof ref === 'function') ref(node);
        else if (ref) ref.current = node;
      }}
      className="reveal"
      style={{
        maxWidth: 1600,
        minHeight: '60vh',
        margin: '0 auto',
        marginTop: '2.5rem',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '0 2rem',
        textAlign: 'left',
        gap: '0.5rem',
        boxSizing: 'border-box',
        width: '100%',
        background: 'transparent'
      }}
    >
      <div
        style={{
          flex: 1.3,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minWidth: 0,
          position: 'relative'
        }}
      >
        <div ref={imgRef} className="logo-glow-container reveal">
          <img
            src={rustpmc3}
            alt="Sobre Rustaco"
            style={{
              width: '100%',
              maxWidth: '900px',
              minWidth: '400px',
              borderRadius: 0,
              boxShadow: 'none',
              objectFit: 'contain',
              background: 'transparent',
              animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)',
              display: 'block',
              position: 'relative',
              zIndex: 2
            }}
          />
          <div className="logo-glow-bg"></div>
        </div>
      </div>
      <div
        style={{
          flex: 1.1,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'flex-start',
          minWidth: 0
        }}
      >
        <div ref={titleRef} className="reveal">
          <h2
            style={{
              fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
              fontWeight: 900,
              color: 'var(--rust-orange)',
              marginBottom: '2rem',
              fontSize: '2.7rem',
              letterSpacing: '1px',
              textAlign: 'left'
            }}
          >
            Sobre Rustaco
          </h2>
        </div>
        <div ref={textRef} className="reveal">
          <p style={{
            color: '#f5f5f5',
            fontFamily: 'Montserrat, Arial, sans-serif',
            fontWeight: 500,
            fontSize: '1.5rem',
            margin: 0,
            maxWidth: 700,
            textAlign: 'left'
          }}>
            Rustaco fue formado por un grupo de amigos con experiencia brutal en Rust, que han decidido crear torneos y eventos competitivos para la comunidad LATAM.
          </p>
        </div>
      </div>
    </section>
  );
});

// NUEVO APARTADO ABAJO DE TODO
const ExtraInfoSection = React.forwardRef((props, ref) => {
  const sectionRef = useRef(null);
  useRevealOnScroll(sectionRef, { threshold: 0.15 });

  return (
    <section
      ref={sectionRef}
      className="reveal"
      style={{
        maxWidth: 900,
        margin: '0 auto',
        marginTop: '3rem',
        marginBottom: '3rem',
        padding: '2.5rem 1.5rem',
        background: 'rgba(24,24,24,0.93)',
        borderRadius: '24px',
        boxShadow: '0 4px 32px #000a',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '1.5rem'
      }}
    >
      <h3 style={{
        color: 'var(--rust-orange)',
        fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
        fontWeight: 800,
        fontSize: '2rem',
        marginBottom: '1rem',
        letterSpacing: '1px'
      }}>
        Información adicional
      </h3>
      <p style={{
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif',
        fontSize: '1.2rem',
        textAlign: 'center',
        margin: 0,
        maxWidth: 700
      }}>
        Pronto anunciaremos más detalles sobre premios, invitados especiales y sorpresas para la comunidad. ¡Mantente atento a nuestras redes sociales!
      </p>
    </section>
  );
});

// APARTADO DE INFOCARDS AL FINAL DE LA WEB
const EventoInfoSection = React.forwardRef((props, ref) => {
  const sectionRef = useRef(null);
  const logoRef = useRef(null);
  const titleRef = useRef(null);
  const cardsRef = useRef([]);

  useRevealOnScroll(sectionRef, { threshold: 0.15 });
  useRevealOnScroll(logoRef, { threshold: 0.15 });
  useRevealOnScroll(titleRef, { threshold: 0.15 });

  useEffect(() => {
    cardsRef.current.forEach(card => {
      if (card) {
        const observer = new window.IntersectionObserver(
          ([entry]) => {
            if (entry.isIntersecting) card.classList.add('reveal-visible');
            else card.classList.remove('reveal-visible');
          },
          { threshold: 0.15 }
        );
        observer.observe(card);
        return () => observer.disconnect();
      }
    });
  }, []);

  const infoCards = [
    {
      label: 'Equipos',
      value: '12 teams',
      icon: '👥'
    },
    {
      label: 'Hora',
      value: '17:00 (GMT-4)',
      icon: '⏰'
    },
    {
      label: 'Fecha',
      value: 'A definir',
      icon: '📅'
    },
    {
      label: 'Modo',
      value: 'Competitivo',
      icon: '🏆'
    },
    {
      label: 'Jugadores',
      value: '96 streamers',
      icon: '🎮'
    }
  ];

  return (
    <section
      ref={sectionRef}
      className="reveal"
      style={{
        maxWidth: 1200,
        margin: '0 auto',
        marginTop: '6rem',
        marginBottom: '3rem',
        padding: '2.5rem 1.5rem',
        background: 'rgba(24,24,24,0.93)',
        borderRadius: '24px',
        boxShadow: '0 4px 32px #000a',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '2.5rem'
      }}
    >
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '2.2rem'
      }}>
        <div ref={logoRef} className="reveal">
          <img
            src={logo}
            alt="Rustaco Logo"
            style={{
              width: 90,
              height: 90,
              borderRadius: '50%',
              objectFit: 'cover',
              boxShadow: '0 2px 18px #000a',
              background: '#23201a',
              border: '3px solid #e25822'
            }}
          />
        </div>
        <div ref={titleRef} className="reveal">
          <h2 style={{
            fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
            fontWeight: 900,
            color: 'var(--rust-orange)',
            fontSize: '2.7rem',
            letterSpacing: '2px',
            margin: 0,
            textShadow: '2px 2px 8px #000a'
          }}>
            Rustaco 2
          </h2>
        </div>
      </div>
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '2rem',
          justifyContent: 'center',
          width: '100%'
        }}
      >
        {infoCards.map((card, idx) => (
          <div
            key={card.label}
            ref={el => (cardsRef.current[idx] = el)}
            className="evento-info-card reveal"
            style={{
              background: '#23201a',
              borderRadius: '18px',
              boxShadow: '0 2px 18px #0008',
              padding: '2rem 2.5rem',
              minWidth: 180,
              minHeight: 120,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '0.7rem',
              border: '2px solid #e25822',
              fontFamily: 'Montserrat, Arial, sans-serif',
              fontWeight: 700,
              fontSize: '1.3rem',
              color: '#fff',
              letterSpacing: '1px'
            }}
          >
            <span style={{ fontSize: '2.2rem' }}>{card.icon}</span>
            <span style={{ color: 'var(--rust-orange)', fontWeight: 900 }}>{card.value}</span>
            <span style={{ fontSize: '1rem', color: '#f39c12', fontWeight: 600 }}>{card.label}</span>
          </div>
        ))}
      </div>
    </section>
  );
});

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => {
  const formatoRef = useRef(null);
  const aboutRef = useRef(null);
  const eventoInfoRef = useRef(null);
  const extraInfoRef = useRef(null);

  const scrollToFormato = () => {
    formatoRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };
  const scrollToAbout = () => {
    aboutRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };

  return (
    <>
      <TopBar onFormatoClick={scrollToFormato} onInfoClick={scrollToAbout} />
      <Header />
      <FormatoSection ref={formatoRef} />
      <AboutSection ref={aboutRef} />
      <ExtraInfoSection ref={extraInfoRef} />
      <EventoInfoSection ref={eventoInfoRef} /> {/* InfoCards al final */}
      <Footer />
    </>
  );
};

export default Home;
