import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

const ruleChapters = [
  {
    number: "1",
    title: "Formato General",
    description: "Lineamientos básicos del torneo.",
    clauses: [
      { code: "1.1", text: "Los equipos deberán estar conformados por ocho jugadores registrados." },
      { code: "1.2", text: "Cada equipo contará con un capitán responsable de la comunicación oficial." }
    ]
  },
  {
    number: "2",
    title: "Ítems prohibidos durante el evento",
    description: "Objetos que no se pueden usar.",
    clauses: [
      { code: "2.1", text: "Durante el evento, los siguientes ítems estarán totalmente prohibidos y no podrán ser usados bajo ninguna circunstancia:" },
      { code: "2.2", text: "Armaduras pesadas (Heavy Plate Helmet, Jacket y Pants)" },
      { code: "2.3", text: "Mira 8x y 16x" },
      { code: "2.4", text: "Rifle L96" },
      { code: "2.5", text: "M4 Shotgun" },
      { code: "2.6", text: "Silenciadores" },
      { code: "2.7", text: "Armored Doors" },
      { code: "2.8", text: "Cualquier otro objeto que pueda ser usado para atacar o destruir" }
    ]
  },
  {
    number: "3",
    title: "Recursos",
    description: "Configuración de recursos minerales.",
    clauses: [
      { code: "3.1", text: "El farmeo de recursos minerales estará configurado a x2.0 durante todo el evento." }
    ]
  },
  {
    number: "4",
    title: "Mejoras de building",
    description: "Mejoras de construcción.",
    clauses: [
      { code: "4.1", text: "Las mejoras de building del evento solo estarán permitidas hasta metal (no se permite HQ)." }
    ]
  },
  {
    number: "5",
    title: "Mecánica principal: Sistema de Dogtags",
    description: "Modo del torneo.",
    clauses: [
      { code: "5.1", text: "El modo del torneo es mediante dogtags únicos por cada jugador." },
      { code: "5.2", text: "Cada vez que mates a un jugador, este soltará un dogtag." },
      { code: "5.3", text: "Para que el dogtag cuente para ganar, debe ser depositado en el 'altar' de la isla respectiva de cada team." }
    ]
  },
  {
    number: "6",
    title: "Eliminación por destrucción del TC",
    description: "Eliminación del equipo.",
    clauses: [
      { code: "6.1", text: "Durante la era de raideos, si el TC (armario principal) de tu equipo es destruido, tu equipo quedará eliminado del evento." }
    ]
  },
  {
    number: "7",
    title: "Estructura y reglas de las islas y bases",
    description: "Configuración de las islas y bases.",
    clauses: [
      { code: "7.1", text: "Cada isla cuenta con:" },
      { code: "7.2", text: "Un espacio designado para la base principal (main)." },
      { code: "7.3", text: "Un área específica para la zona de huerto/garage (9x8). No se aceptan bases con MultiTC (solo un TC por base principal y huerto)." },
      { code: "7.4", text: "Una entrada a metro exclusiva para cada isla." },
      { code: "7.5", text: "Un altar exclusivo para cada isla donde el equipo debe depositar sus dogtags para que cuenten." }
    ]
  },
  {
    number: "8",
    title: "Evento por Eras",
    description: "Eras del evento.",
    clauses: [
      { code: "8.1", text: "El evento está estructurado en eras, cada una con sus propias reglas y restricciones." }
    ]
  },
  {
    number: "9",
    title: "Límite de SAM Turrets y Torretas de Fuego",
    description: "Límites de torretas.",
    clauses: [
      { code: "9.1", text: "Cada base puede tener un máximo de 1 SAM Turret colocada." },
      { code: "9.2", text: "Las Torretas de Fuego siguen estando totalmente prohibidas durante el evento." }
    ]
  },
  {
    number: "10",
    title: "Límite de torretas y trampas",
    description: "Límites de torretas y trampas.",
    clauses: [
      { code: "10.1", text: "El uso de torretas está limitado a un máximo de 12 Auto Turrets y 12 Escopetas Trampa por TC." }
    ]
  },
  {
    number: "11",
    title: "Parcelas especiales alrededores del mapa",
    description: "Parcelas especiales.",
    clauses: [
      { code: "11.1", text: "Por los alrededores del mapa, cerca de los monumentos principales, habrá mini parcelas que los equipos podrán tomar y construir lo que deseen dentro de esa base. Estas parcelas podrán ser raideadas en cualquier momento del evento, sin restricción de era." }
    ]
  },
  {
    number: "12",
    title: "Sanciones y Conducta",
    description: "Criterios de penalización durante el torneo.",
    clauses: [
      { code: "12.1", text: "El staff podrá revisar evidencias y aplicar sanciones en cualquier momento." },
      { code: "12.2", text: "Toda denuncia debe incluir material verificable; sin evidencia la denuncia no procede." },
      { code: "12.3", text: "Las decisiones del equipo de administración son definitivas durante el evento." }
    ]
  }
];

export default function Reglas() {
  React.useEffect(() => {
    document.body.classList.remove("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  const handlePrint = () => window.print();

  return (
    <div className="page rules-page">
      <header className="page-header">
        <Link className="nav-btn" to="/">Volver al inicio</Link>
        <h1>Reglamento Rustaco</h1>
        <button type="button" className="nav-btn" onClick={handlePrint}>
          Imprimir / PDF
        </button>
      </header>
      <main>
        {ruleChapters.map((chapter) => (
          <section key={chapter.number} className="rule-section">
            <h2>{`Regla ${chapter.number}: ${chapter.title}`}</h2>
            <p>{chapter.description}</p>
            <ol>
              {chapter.clauses.map((clause) => (
                <li key={clause.code} id={`regla-${clause.code}`}>
                  <strong>{clause.code}</strong> — {clause.text}
                </li>
              ))}
            </ol>
          </section>
        ))}
      </main>
    </div>
  );
}
