import React from 'react';
import logo from '../assets/img/logorustaco.png';
import '../assets/styles.css';

// Navbar con logo centrado y botones a los costados, solo logo (más grande, sin texto)
const Navbar = () => (
  <nav className="navbar navbar-centered">
    <div className="navbar-links navbar-links-left">
      <a href="/">Inicio</a>
      <a href="/events">Eventos</a>
    </div>
    <div className="navbar-logo navbar-logo-center">
      <img
        src={logo}
        alt="Rustaco Logo"
        className="logo-animated logo-large"
        height={90}
        width={90}
        style={{
          borderRadius: '50%',
          background: 'rgba(35,32,26,0.95)',
          boxShadow: '0 4px 24px #000a, 0 0 0 4px #e25822'
        }}
      />
    </div>
    <div className="navbar-links navbar-links-right">
      <a href="/about">Nosotros</a>
      <a href="/contact">Contacto</a>
    </div>
  </nav>
);

const Header = () => (
  <header className="header">
    <h1>Bienvenido a Rustaco Eventos</h1>
    <p>Organización profesional de torneos y actividades inspiradas en el universo Rust</p>
  </header>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <Navbar />
    <Header />
    <main>
      <section style={{textAlign: 'center', margin: '3rem 0'}}>
        <h2 style={{color: 'var(--rust-orange)', fontSize: '2rem'}}>¡Descubre la experiencia Rustaco!</h2>
        <p style={{fontSize: '1.2rem', color: 'var(--rust-accent)'}}>
          Participa en torneos, talleres y encuentros únicos para la comunidad Rust.<br />
          Explora nuestras secciones para más información.
        </p>
      </section>
    </main>
    <Footer />
  </>
);

export default Home;
