import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const initialPlayers = Array.from({ length: 8 }, () => ({
  name: '',
  steamid: '',
  twitch: ''
}));

const Applys = () => {
  const [user, setUser] = useState(undefined); // undefined para loading
  const [players, setPlayers] = useState(initialPlayers);
  const [teamName, setTeamName] = useState('');
  const [captain, setCaptain] = useState('');
  const [discord, setDiscord] = useState('');
  const [why, setWhy] = useState('');
  const [strategy, setStrategy] = useState('');
  const [terms, setTerms] = useState(false);
  const [acceptTerms, setAcceptTerms] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState('');
  const history = useHistory();

  useEffect(() => {
    fetch('http://192.168.1.178:3001/api/user', { credentials: 'include' })
      .then(res => res.json())
      .then(data => setUser(data && data.steamid ? data : null))
      .catch(() => setUser(null));
  }, []);

  const handlePlayerChange = (idx, field, value) => {
    setPlayers(players =>
      players.map((p, i) => (i === idx ? { ...p, [field]: value } : p))
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError('');
    if (!terms) {
      setError('Debes aceptar los términos y condiciones para inscribirte.');
      return;
    }
    if (!teamName || !captain || !discord || players.some(p => !p.name || !p.steamid || !p.twitch)) {
      setError('Completa todos los campos obligatorios.');
      return;
    }
    setSubmitted(true);
  };

  if (user === undefined) {
    return (
      <div style={{
        maxWidth: 600,
        margin: '4rem auto',
        background: 'rgba(24,24,24,0.97)',
        borderRadius: 24,
        boxShadow: '0 8px 32px #000b',
        padding: '2.5rem 2rem',
        textAlign: 'center',
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif'
      }}>
        Cargando...
      </div>
    );
  }

  if (!user) {
    return (
      <div style={{
        maxWidth: 600,
        margin: '4rem auto',
        background: 'rgba(24,24,24,0.97)',
        borderRadius: 24,
        boxShadow: '0 8px 32px #000b',
        padding: '2.5rem 2rem',
        textAlign: 'center',
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif'
      }}>
        <h2 style={{ color: '#e25822', fontWeight: 900, fontSize: '2rem', marginBottom: '1.5rem' }}>
          Debes logearte primero para poder ingresar al proceso de inscripción
        </h2>
        <button
          onClick={() => history.push('/')}
          style={{
            marginTop: 24,
            background: '#27ae60',
            color: '#fff',
            fontWeight: 700,
            fontSize: '1rem',
            padding: '0.7rem 2rem',
            border: 'none',
            borderRadius: 10,
            boxShadow: '0 2px 8px #0007',
            cursor: 'pointer',
            letterSpacing: '1px',
            textDecoration: 'none',
            transition: 'background 0.2s, transform 0.2s'
          }}
        >
          Volver al Home
        </button>
      </div>
    );
  }

  if (submitted) {
    return (
      <div style={{
        maxWidth: 600,
        margin: '4rem auto',
        background: 'rgba(24,24,24,0.97)',
        borderRadius: 24,
        boxShadow: '0 8px 32px #000b',
        padding: '2.5rem 2rem',
        textAlign: 'center',
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif'
      }}>
        <h2 style={{ color: '#27ae60', fontWeight: 900, fontSize: '2rem', marginBottom: '1.5rem' }}>
          ¡Inscripción enviada!
        </h2>
        <p>Tu equipo ha sido inscrito correctamente. Pronto recibirás novedades en Discord o por correo.</p>
        <button
          onClick={() => history.push('/')}
          style={{
            marginTop: 24,
            background: '#e25822',
            color: '#fff',
            fontWeight: 700,
            fontSize: '1rem',
            padding: '0.7rem 2rem',
            border: 'none',
            borderRadius: 10,
            boxShadow: '0 2px 8px #0007',
            cursor: 'pointer',
            letterSpacing: '1px',
            textDecoration: 'none',
            transition: 'background 0.2s, transform 0.2s'
          }}
        >
          Volver al Home
        </button>
      </div>
    );
  }

  // Pantalla de aceptación de términos antes del formulario
  if (!acceptTerms) {
    return (
      <div style={{
        maxWidth: 650,
        margin: '4rem auto',
        background: 'rgba(24,24,24,0.99)',
        borderRadius: 28,
        boxShadow: '0 8px 32px #000b',
        padding: '2.8rem 2.2rem',
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif',
        textAlign: 'center'
      }}>
        <h1 style={{
          color: '#e25822',
          fontWeight: 900,
          fontSize: '2.1rem',
          marginBottom: '1.5rem',
          letterSpacing: '2px',
          textShadow: '0 2px 18px #000a'
        }}>
          Proceso de Inscripción - Rustaco II
        </h1>
        <div style={{
          background: 'rgba(34,34,34,0.97)',
          borderRadius: 18,
          padding: '1.5rem 1.2rem',
          marginBottom: '1.5rem',
          fontSize: '1.13rem',
          color: '#fff',
          boxShadow: '0 2px 12px #0007',
          textAlign: 'left'
        }}>
          <b>Antes de continuar, debes aceptar los términos y condiciones del evento:</b>
          <ul style={{ margin: '1.1rem 0 0 1.2rem', fontSize: '1.08rem', color: '#f39c12', lineHeight: 1.6 }}>
            <li>He leído y acepto el <a href="/reglas" target="_blank" rel="noopener noreferrer" style={{ color: '#7289da', textDecoration: 'underline' }}>reglamento oficial</a> del torneo Rustaco II.</li>
            <li>Me comprometo a que todos los datos entregados son verídicos y que los integrantes del equipo están informados y de acuerdo con la inscripción.</li>
            <li>Entiendo que el incumplimiento de las reglas puede resultar en la descalificación del equipo.</li>
            <li>Acepto que la administración podrá contactarme vía Discord para cualquier comunicación oficial.</li>
            <li>Declaro que todos los participantes tienen cuenta de Steam y cumplen los requisitos del evento.</li>
            <li>La organización se reserva el derecho de admisión y puede solicitar información adicional si lo estima conveniente.</li>
          </ul>
        </div>
        <button
          onClick={() => setAcceptTerms(true)}
          style={{
            background: 'linear-gradient(90deg, #27ae60 60%, #23201a 100%)',
            color: '#fff',
            fontWeight: 800,
            fontSize: '1.18rem',
            padding: '0.9rem 2.5rem',
            border: 'none',
            borderRadius: 14,
            boxShadow: '0 2px 12px #0007',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.2s',
            letterSpacing: '1px',
            marginTop: 8
          }}
          onMouseOver={e => (e.currentTarget.style.transform = 'scale(1.07)')}
          onMouseOut={e => (e.currentTarget.style.transform = 'scale(1)')}
        >
          Acepto y deseo inscribir a mi equipo
        </button>
      </div>
    );
  }

  // Formulario de inscripción profesional
  return (
    <div style={{
      maxWidth: 700,
      margin: '4rem auto',
      background: 'rgba(24,24,24,0.97)',
      borderRadius: 24,
      boxShadow: '0 8px 32px #000b',
      padding: '2.5rem 2rem',
      color: '#fff',
      fontFamily: 'Montserrat, Arial, sans-serif'
    }}>
      <h1 style={{
        color: '#e25822',
        fontWeight: 900,
        fontSize: '2.2rem',
        marginBottom: '1.5rem',
        letterSpacing: '2px',
        textShadow: '0 2px 18px #000a',
        textAlign: 'center'
      }}>
        Inscripción al Torneo Rustaco II
      </h1>
      <form onSubmit={handleSubmit} style={{ marginTop: 24 }}>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>Nombre del Equipo *</label>
          <input
            type="text"
            value={teamName}
            onChange={e => setTeamName(e.target.value)}
            style={{
              width: '100%',
              padding: '0.7rem',
              borderRadius: 8,
              border: '1.5px solid #e25822',
              marginTop: 6,
              fontSize: '1.08rem',
              background: '#181818',
              color: '#fff'
            }}
            required
          />
        </div>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>Capitán del Equipo *</label>
          <input
            type="text"
            value={captain}
            onChange={e => setCaptain(e.target.value)}
            style={{
              width: '100%',
              padding: '0.7rem',
              borderRadius: 8,
              border: '1.5px solid #e25822',
              marginTop: 6,
              fontSize: '1.08rem',
              background: '#181818',
              color: '#fff'
            }}
            required
          />
        </div>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>Discord del Capitán (será contactado por la administración) *</label>
          <input
            type="text"
            value={discord}
            onChange={e => setDiscord(e.target.value)}
            style={{
              width: '100%',
              padding: '0.7rem',
              borderRadius: 8,
              border: '1.5px solid #7289da',
              marginTop: 6,
              fontSize: '1.08rem',
              background: '#181818',
              color: '#fff'
            }}
            placeholder="Ejemplo: usuario#1234"
            required
          />
        </div>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>Participantes del Equipo (8 jugadores, capitán incluido) *</label>
          <table style={{ width: '100%', marginTop: 8, marginBottom: 8, borderCollapse: 'collapse' }}>
            <thead>
              <tr>
                <th style={{ color: '#fff', fontWeight: 600, fontSize: '1rem', padding: 6 }}>Nombre</th>
                <th style={{ color: '#fff', fontWeight: 600, fontSize: '1rem', padding: 6 }}>SteamID</th>
                <th style={{ color: '#fff', fontWeight: 600, fontSize: '1rem', padding: 6 }}>Canal de Twitch</th>
              </tr>
            </thead>
            <tbody>
              {players.map((p, idx) => (
                <tr key={idx}>
                  <td>
                    <input
                      type="text"
                      value={p.name}
                      onChange={e => handlePlayerChange(idx, 'name', e.target.value)}
                      style={{
                        width: '98%',
                        padding: '0.5rem',
                        borderRadius: 6,
                        border: '1px solid #7289da',
                        background: '#181818',
                        color: '#fff'
                      }}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={p.steamid}
                      onChange={e => handlePlayerChange(idx, 'steamid', e.target.value)}
                      style={{
                        width: '98%',
                        padding: '0.5rem',
                        borderRadius: 6,
                        border: '1px solid #7289da',
                        background: '#181818',
                        color: '#fff'
                      }}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={p.twitch}
                      onChange={e => handlePlayerChange(idx, 'twitch', e.target.value)}
                      style={{
                        width: '98%',
                        padding: '0.5rem',
                        borderRadius: 6,
                        border: '1px solid #7289da',
                        background: '#181818',
                        color: '#fff'
                      }}
                      required
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>¿Por qué cree que debe participar en este evento? *</label>
          <textarea
            value={why}
            onChange={e => setWhy(e.target.value)}
            style={{
              width: '100%',
              padding: '0.7rem',
              borderRadius: 8,
              border: '1.5px solid #e25822',
              marginTop: 6,
              fontSize: '1.08rem',
              background: '#181818',
              color: '#fff',
              minHeight: 60
            }}
            required
          />
        </div>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>
            ¿Qué estrategia o preparación especial tiene su equipo para destacar en el torneo?
          </label>
          <textarea
            value={strategy}
            onChange={e => setStrategy(e.target.value)}
            style={{
              width: '100%',
              padding: '0.7rem',
              borderRadius: 8,
              border: '1.5px solid #e25822',
              marginTop: 6,
              fontSize: '1.08rem',
              background: '#181818',
              color: '#fff',
              minHeight: 60
            }}
            placeholder="Describe brevemente la preparación, entrenamientos, roles, etc."
          />
        </div>
        <div style={{ marginBottom: 18 }}>
          <label style={{ fontWeight: 700, color: '#f39c12' }}>
            <input
              type="checkbox"
              checked={terms}
              onChange={e => setTerms(e.target.checked)}
              style={{ marginRight: 8, accentColor: '#e25822' }}
              required
            />
            Confirmo que he leído y acepto los <a href="/reglas" target="_blank" rel="noopener noreferrer" style={{ color: '#7289da', textDecoration: 'underline' }}>términos y condiciones</a> y me comprometo a respetar todas las reglas del evento.
          </label>
        </div>
        {error && (
          <div style={{ color: '#e25822', fontWeight: 700, marginBottom: 12 }}>{error}</div>
        )}
        <button
          type="submit"
          style={{
            background: 'linear-gradient(90deg, #27ae60 60%, #23201a 100%)',
            color: '#fff',
            fontWeight: 700,
            fontSize: '1.18rem',
            padding: '0.9rem 2.5rem',
            border: 'none',
            borderRadius: 14,
            boxShadow: '0 2px 12px #0007',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.2s',
            letterSpacing: '1px',
            marginTop: 8
          }}
          onMouseOver={e => (e.currentTarget.style.transform = 'scale(1.07)')}
          onMouseOut={e => (e.currentTarget.style.transform = 'scale(1)')}
        >
          Enviar inscripción
        </button>
      </form>
    </div>
  );
};

export default Applys;
