import React from 'react';

import eraPrimitivo from '../assets/img/primitivo.png';
import eraTier1 from '../assets/img/tier1.png';
import eraTier2 from '../assets/img/tier2.png';
import eraTier3 from '../assets/img/tier3.png';
import eraRaids from '../assets/img/raids.png';
import samsite from '../assets/img/samsite.png';
import flameturret from '../assets/img/flameturret.png';
import autoturret from '../assets/img/autoturret.png';
import guntrap from '../assets/img/guntrap.png';
import buildingexterior from '../assets/img/buildingexterior.png';
import heavyHelmet from '../assets/img/heavy.plate.helmet.png';
import heavyJacket from '../assets/img/heavy.plate.jacket.png';
import heavyPants from '../assets/img/heavy.plate.pants.png';
import scope8x from '../assets/img/weapon.mod.8x.scope.png';
import muzzleBoost from '../assets/img/weapon.mod.muzzleboost.png';
import oilFilterSilencer from '../assets/img/weapon.mod.oilfiltersilencer.png';
import silencer from '../assets/img/weapon.mod.silencer.png';
import sodaCanSilencer from '../assets/img/weapon.mod.sodacansilencer.png';

const SideAnimation = ({ side = 'left' }) => (
  <div
    style={{
      position: 'absolute',
      top: 0,
      [side]: 0,
      height: '100%',
      width: 60,
      pointerEvents: 'none',
      zIndex: 0,
      display: 'flex',
      alignItems: 'center',
      justifyContent: side === 'left' ? 'flex-start' : 'flex-end',
      background: 'none'
    }}
  >
    <div
      style={{
        width: 8,
        height: '80%',
        margin: 'auto',
        borderRadius: 12,
        background: 'linear-gradient(180deg, #e25822 0%, #23201a 100%)',
        boxShadow: '0 0 32px 8px #e2582255, 0 0 0 0 #0000',
        animation: `sideGlow-${side} 2.5s ease-in-out infinite alternate`
      }}
    />
    <style>
      {`
        @keyframes sideGlow-left {
          0% { box-shadow: 0 0 32px 8px #e2582255, 0 0 0 0 #0000; }
          100% { box-shadow: 0 0 64px 24px #e25822cc, 0 0 0 0 #0000; }
        }
        @keyframes sideGlow-right {
          0% { box-shadow: 0 0 32px 8px #7289da55, 0 0 0 0 #0000; }
          100% { box-shadow: 0 0 64px 24px #7289dacc, 0 0 0 0 #0000; }
        }
      `}
    </style>
  </div>
);

const eras = [
  {
    nombre: "Era Primitivo",
    imagen: eraPrimitivo,
    descripcion: "Comienzo del evento con armas y herramientas primitivas."
  },
  {
    nombre: "Era Tier 1",
    imagen: eraTier1,
    descripcion: "Acceso a equipamiento y armas de Tier 1."
  },
  {
    nombre: "Era Tier 2",
    imagen: eraTier2,
    descripcion: "Se habilitan armas y objetos de Tier 2."
  },
  {
    nombre: "Era Tier 3",
    imagen: eraTier3,
    descripcion: "Desbloqueo de todo el arsenal y equipamiento de Tier 3."
  },
  {
    nombre: "Era Raids",
    imagen: eraRaids,
    descripcion: "Comienza la era de raideos, se habilitan explosivos y ataques a bases."
  }
];

const reglaCardStyle = {
  background: 'linear-gradient(120deg, #23201a 80%, #2d2d2d 100%)',
  borderRadius: '16px',
  boxShadow: '0 4px 24px #000a',
  padding: '1.6rem 2rem',
  marginBottom: '2rem',
  borderLeft: '6px solid var(--rust-orange)',
  borderRight: '2px solid #7289da55',
  transition: 'box-shadow 0.2s',
  position: 'relative',
  zIndex: 1
};

const eraCardStyle = {
  background: 'linear-gradient(120deg, #23201a 80%, #3a4bd8 100%)',
  borderRadius: '14px',
  boxShadow: '0 2px 16px #000a',
  padding: '1.2rem 1.2rem',
  marginBottom: '1.2rem',
  display: 'flex',
  alignItems: 'center',
  gap: '1.5rem',
  borderLeft: '5px solid #7289da',
};

const iconStyle = {
  width: 38,
  height: 38,
  objectFit: 'contain',
  verticalAlign: 'middle',
  marginRight: 10,
  background: '#181818',
  borderRadius: 8,
  boxShadow: '0 1px 8px #0007'
};

const reglas = [
  {
    titulo: "1. Formato general",
    descripcion: (
      <ul style={{ margin: 0, paddingLeft: '1.5em', listStyle: 'disc' }}>
        <li><b>112 jugadores</b> en total.</li>
        <li><b>Equipos de 8 participantes</b>.</li>
        <li>Se realizar√°n <b>diversos eventos especiales</b> durante el evento.</li>
        <li><b>Fishing Bandits</b>.</li>
      </ul>
    )
  },
  {
    titulo: "2. √çtems prohibidos durante el evento",
    descripcion: (
      <div>
        Durante el evento, los siguientes √≠tems estar√°n <b>totalmente prohibidos</b> y no podr√°n ser usados bajo ninguna circunstancia:
        <ul style={{ margin: '10px 0 0 1.5em', padding: 0, listStyle: 'disc' }}>
          <li>
            <img src={heavyHelmet} alt="Heavy Plate Helmet" style={iconStyle} />
            <img src={heavyJacket} alt="Heavy Plate Jacket" style={iconStyle} />
            <img src={heavyPants} alt="Heavy Plate Pants" style={iconStyle} />
            Armaduras pesadas (<b>Heavy Plate Helmet, Jacket y Pants</b>)
          </li>
          <li>
            <img src={scope8x} alt="8x Scope" style={iconStyle} />
            Mira 8x (<b>8x Scope</b>)
          </li>
          <li>
            <img src={muzzleBoost} alt="Muzzle Boost" style={iconStyle} />
            Muzzle Boost (<b>Bocacha de aceleraci√≥n</b>)
          </li>
          <li>
            <img src={oilFilterSilencer} alt="Oil Filter Silencer" style={iconStyle} />
            Oil Filter Silencer (<b>Silenciador de filtro de aceite</b>)
          </li>
          <li>
            <img src={silencer} alt="Silencer" style={iconStyle} />
            Silencer (<b>Silenciador est√°ndar</b>)
          </li>
          <li>
            <img src={sodaCanSilencer} alt="Soda Can Silencer" style={iconStyle} />
            Soda Can Silencer (<b>Silenciador de lata</b>)
          </li>
        </ul>
        <span style={{ color: "#f39c12" }}>
          El uso de cualquiera de estos √≠tems resultar√° en sanci√≥n inmediata para el jugador y/o el equipo.
        </span>
      </div>
    )
  },
  {
    titulo: "3. Mec√°nica principal: Sistema de Dogtags",
    descripcion: (
      <span>
        Cada jugador, al ser eliminado, soltar√° un <b>dogtag</b> (placa identificatoria). Los equipos deber√°n recolectar los dogtags de sus rivales y depositarlos en el <b>armario principal</b> de su base para que sean contabilizados. <br />
        <span style={{ color: "#f39c12" }}>Solo los dogtags depositados correctamente sumar√°n puntos para el equipo.</span>
      </span>
    )
  },
  {
    titulo: "4. Eliminaci√≥n por destrucci√≥n del TC",
    descripcion: (
      <span>
        Durante la <b>era de raideos</b>, si el <b>TC (armario principal)</b> de tu equipo es destruido, <span style={{ color: "#e25822", fontWeight: 700 }}>tu equipo quedar√° eliminado</span> del evento.
      </span>
    )
  },
  {
    titulo: "5. Estructura y reglas de las islas y bases",
    descripcion: (
      <ul style={{ margin: 0, paddingLeft: '1.5em', listStyle: 'disc' }}>
        <li>
          Cada isla cuenta con:
          <ul style={{ margin: 0, paddingLeft: '1.5em', listStyle: 'circle' }}>
            <li>Un espacio designado para la <b>base principal (main)</b>.</li>
            <li>Un √°rea espec√≠fica para la <b>zona de huerto</b> (6x7).</li>
            <li>Una <b>entrada a metro</b> exclusiva para cada isla.</li>
          </ul>
        </li>
        <li>
          La <b>base main</b> debe construirse √∫nicamente en el espacio asignado (14x14). <br />
          <span style={{ color: "#f39c12" }}>No est√° permitido sobrepasar los l√≠mites de la zona asignada, incluyendo la zona de huerto.</span>
        </li>
        <li>
          El <b>TC (armario principal)</b> de la base main debe ser accesible en todo momento. Solo puede estar protegido por puertas o ventanas, <b>no puede estar completamente cerrado ni grifeado</b>.
        </li>
        <li>
          El <b>TC</b> puede ser movido en caso de ser necesario, pero <b>debe solicitarse el cambio a la administraci√≥n</b>. <br />
          <span style={{ color: "#f39c12" }}>El TC main solo podr√° ser cambiado hasta 1 hora antes del inicio de la era de raideos.</span>
        </li>
      </ul>
    )
  },
  // ...resto de reglas igual...
  {
    titulo: "6. Evento por Eras",
    descripcion: (
      <div>
        <div style={{ marginBottom: 18 }}>
          Todos los participantes deben mantener una actitud respetuosa y deportiva hacia otros jugadores, organizadores y espectadores. Cualquier conducta t√≥xica, insultos o discriminaci√≥n resultar√° en sanciones o expulsi√≥n.
          <br /><br />
          <b>IMPORTANTE:</b> El respeto se extiende tambi√©n al uso de chats de voz y texto, redes sociales y cualquier medio relacionado con el evento. La organizaci√≥n promueve un ambiente sano y seguro para todos los participantes.
        </div>
        <div>
          <h3 style={{
            color: '#7289da',
            fontWeight: 800,
            fontSize: '1.15rem',
            margin: '1.2rem 0 0.7rem 0',
            textAlign: 'left',
            letterSpacing: '1px'
          }}>
            Eras del Evento
          </h3>
          {eras.map((era, idx) => (
            <div key={idx} style={eraCardStyle}>
              <img
                src={era.imagen}
                alt={era.nombre}
                style={{
                  width: 70,
                  height: 70,
                  objectFit: 'contain',
                  borderRadius: 12,
                  background: '#181818',
                  boxShadow: '0 1px 8px #0007'
                }}
              />
              <div>
                <div style={{ fontWeight: 700, fontSize: '1.08rem', color: '#b3cfff', marginBottom: 4 }}>
                  {era.nombre}
                </div>
                <div style={{ fontSize: '1.01rem', color: '#e0e0e0' }}>
                  {era.descripcion}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    )
  },
  // ...contin√∫a el resto de reglas igual...
];

const Reglas = () => (
  <div style={{
    maxWidth: 950,
    margin: '3rem auto',
    background: 'rgba(24,24,24,0.98)',
    borderRadius: 28,
    boxShadow: '0 12px 48px #000c',
    padding: '2.7rem 2.2rem',
    color: '#fff',
    fontFamily: 'Montserrat, Arial, sans-serif',
    border: '1.5px solid #e2582233',
    position: 'relative',
    overflow: 'hidden'
  }}>
    {/* Animaciones laterales */}
    <SideAnimation side="left" />
    <SideAnimation side="right" />

    <h1 style={{
      color: 'var(--rust-orange)',
      fontWeight: 900,
      fontSize: '2.5rem',
      marginBottom: '2.2rem',
      textAlign: 'center',
      letterSpacing: '2px',
      textShadow: '0 2px 12px #000a',
      zIndex: 2,
      position: 'relative'
    }}>
      Reglas del Evento
    </h1>
    <div>
      {reglas.map((regla, idx) => (
        <div key={idx} style={reglaCardStyle}>
          <div style={{
            fontWeight: 800,
            color: '#f39c12',
            fontSize: '1.18rem',
            marginBottom: 6,
            letterSpacing: '1px'
          }}>
            {regla.titulo}
          </div>
          <div style={{ fontSize: '1.08rem', color: '#e0e0e0', lineHeight: 1.7 }}>
            {regla.descripcion}
          </div>
        </div>
      ))}
    </div>
    <div style={{
      marginTop: '2.5rem',
      padding: '1.2rem',
      background: 'linear-gradient(90deg, #181818 80%, #23201a 100%)',
      borderRadius: 14,
      color: '#b3cfff',
      fontSize: '1.08rem',
      textAlign: 'center',
      boxShadow: '0 2px 12px #0007',
      border: '1px solid #7289da44'
    }}>
      <b>Nota:</b> El incumplimiento de estas reglas puede resultar en sanciones, expulsi√≥n del evento o baneo permanente de futuras competencias.
      <br />
      Para dudas o consultas, contacta a la organizaci√≥n en nuestro <a href="https://discord.gg/rustaco" target="_blank" rel="noopener noreferrer" style={{ color: '#7289da', textDecoration: 'underline' }}>Discord oficial</a>.
    </div>
    <div style={{
      marginTop: '2.5rem',
      display: 'flex',
      justifyContent: 'center'
    }}>
      <button
        onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
        style={{
          background: 'linear-gradient(90deg, #e25822 60%, #7289da 100%)',
          color: '#fff',
          fontWeight: 700,
          fontSize: '1.1rem',
          padding: '0.8rem 2.2rem',
          border: 'none',
          borderRadius: 12,
          boxShadow: '0 2px 12px #0007',
          cursor: 'pointer',
          transition: 'background 0.2s, transform 0.2s',
        }}
        onMouseOver={e => e.currentTarget.style.transform = 'scale(1.05)'}
        onMouseOut={e => e.currentTarget.style.transform = 'scale(1)'}
      >
        Volver al inicio
      </button>
    </div>
    </div>
);

export default Reglas;
