import React from "react";
import "../assets/styles.css";
import "../assets/home-dark.css";

import logoNuevo from "../assets/img/logonuevo.png";
import lootroomTextLogo from "../assets/img/lootroomlogo.png";
import syvarLogo from "../assets/img/syvarlogo.png";
import rustypotTextLogo from "../assets/img/rustypotlogo.png";

export default function Home() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div
      className="home-dark has-video-bg"
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "flex-start",
        overflow: "hidden",
        padding: "2vh 0 0",
        gap: "4vh",
      }}
    >
      <main
        className="home-hero"
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "flex-start",
          gap: "0.5rem",
          paddingTop: "3vh",
          paddingBottom: 0,
          flex: 0,
        }}
      >
        <img
          className="home-hero-logo"
          src={logoNuevo}
          alt="Rustaco"
          style={{ width: "min(68vw, 420px)" }}
        />
        <p
          style={{
            margin: 0,
            fontSize: "1.45rem",
            fontWeight: 800,
            letterSpacing: "6px",
            textTransform: "uppercase",
            color: "#ffffffd0",
            textShadow: "0 6px 26px rgba(0,0,0,0.8)",
          }}
        >
          Soon..
        </p>
      </main>

      <footer
        style={{
          position: "relative",
          zIndex: 20,
          marginTop: "1vh",
          padding: "0.5rem 1rem 0.7rem",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          gap: 14,
          background:
            "linear-gradient(90deg, rgba(8,8,10,0.68), rgba(0,0,0,0))",
          flexWrap: "wrap",
        }}
      >
        <img
          src={syvarLogo}
          alt="Syvar"
          style={{ width: 110, height: "auto", objectFit: "contain" }}
        />
        <img
          src={lootroomTextLogo}
          alt="Lootroom"
          style={{ width: 128, height: "auto", objectFit: "contain" }}
        />
        <img
          src={rustypotTextLogo}
          alt="Rustypot"
          style={{ width: 110, height: "auto", objectFit: "contain" }}
        />
      </footer>
    </div>
  );
}
