import React from 'react';
import logo from '../assets/img/logorustaco.png';
import '../assets/styles.css';

// Navbar más delgada, logo sobresaliente hacia abajo y botones alrededor del logo
const Navbar = () => (
  <nav className="navbar navbar-centered navbar-thin" style={{ position: 'relative', minHeight: '70px', padding: 0 }}>
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%',
        gap: '2.2rem',
        position: 'relative',
        paddingTop: '1rem',
        paddingBottom: '1rem'
      }}
    >
      <a href="/" className="nav-btn">Inicio</a>
      <a href="/events" className="nav-btn">Eventos</a>
      <span className="logo-navbar-wrapper">
        <img
          src={logo}
          alt="Rustaco Logo"
          className="logo-animated logo-navbar-overlap"
          height={82}
          width={82}
        />
      </span>
      <a href="/about" className="nav-btn">Nosotros</a>
      <a href="/contact" className="nav-btn">Contacto</a>
    </div>
  </nav>
);

const Header = () => (
  <header className="header" style={{ marginTop: '1.5rem', padding: '1.2rem 0 0.5rem 0' }}>
    <h1 style={{
      fontSize: '2.1rem',
      color: 'var(--rust-orange)',
      letterSpacing: '2px',
      marginBottom: '0.5rem',
      textShadow: '2px 2px 8px #000a'
    }}>
      Bienvenido a Rustaco Eventos
    </h1>
    <p style={{ color: 'var(--rust-accent)', fontSize: '1.1rem' }}>
      Organización profesional de torneos y actividades inspiradas en el universo Rust
    </p>
  </header>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <Navbar />
    <Header />
    <main>
      <section style={{ textAlign: 'center', margin: '2.2rem 0' }}>
        <h2 style={{ color: 'var(--rust-orange)', fontSize: '1.5rem' }}>¡Descubre la experiencia Rustaco!</h2>
        <p style={{ fontSize: '1.1rem', color: 'var(--rust-accent)' }}>
          Participa en torneos, talleres y encuentros únicos para la comunidad Rust.<br />
          Explora nuestras secciones para más información.
        </p>
      </section>
    </main>
    <Footer />
  </>
);

export default Home;
