import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

const ruleChapters = [
  {
    number: "1",
    title: "Formato General",
    description: "Lineamientos básicos del torneo.",
    clauses: [
      { code: "1.1", text: "Los equipos deben registrar ocho jugadores titulares antes del inicio del evento." },
      { code: "1.2", text: "Cada capitán es responsable de la comunicación oficial con la administración." },
      { code: "1.3", text: "Los partidos se disputan en servidores proporcionados por la organización." }
    ]
  },
  {
    number: "2",
    title: "Cronograma y Horarios",
    description: "Tiempos de inicio y tolerancias.",
    clauses: [
      { code: "2.1", text: "Los equipos deben presentarse 20 minutos antes del horario asignado." },
      { code: "2.2", text: "La tolerancia máxima para iniciar un encuentro es de 10 minutos." },
      { code: "2.3", text: "Retrasos adicionales pueden resultar en sanciones o pérdida del encuentro." }
    ]
  },
  {
    number: "12",
    title: "Sanciones y Conducta",
    description: "Criterios de penalización durante el torneo.",
    clauses: [
      { code: "12.1", text: "El staff puede revisar evidencias y aplicar sanciones en cualquier momento." },
      { code: "12.2", text: "Toda denuncia debe incluir material verificable; sin evidencia la denuncia no procede." },
      { code: "12.3", text: "Las decisiones del equipo de administración son definitivas durante el evento." }
    ]
  }
];

export default function Reglas() {
  React.useEffect(() => {
    document.body.classList.remove("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  const handlePrint = React.useCallback(() => window.print(), []);

  return (
    <div className="page rules-page" style={{ padding: "2rem 1.5rem", maxWidth: 960, margin: "0 auto" }}>
      <header
        className="rules-header"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          gap: "1.25rem",
          flexWrap: "wrap",
          marginBottom: "2rem"
        }}
      >
        <div style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
          <Link className="nav-btn" to="/">Volver al inicio</Link>
          <h1 style={{ margin: 0, fontSize: "2rem" }}>Reglamento Rustaco</h1>
        </div>
        <button type="button" className="nav-btn rules-print-btn" onClick={handlePrint}>
          Imprimir / PDF
        </button>
      </header>

      <main style={{ display: "flex", flexDirection: "column", gap: "2.5rem" }}>
        {ruleChapters.map((chapter) => (
          <section key={chapter.number} className="rule-section" style={{ display: "grid", gap: "0.75rem" }}>
            <h2 style={{ margin: 0 }}>{`Regla ${chapter.number}: ${chapter.title}`}</h2>
            <p style={{ margin: 0, color: "rgba(255,255,255,0.7)" }}>{chapter.description}</p>
            <ol style={{ margin: 0, paddingLeft: "1.5rem", display: "grid", gap: "0.55rem" }}>
              {chapter.clauses.map((clause) => (
                <li key={clause.code} id={`regla-${clause.code}`} style={{ lineHeight: 1.45 }}>
                  <strong>{clause.code}</strong> — {clause.text}
                </li>
              ))}
            </ol>
          </section>
        ))}
      </main>
    </div>
  );
}
