import React from "react";
import { useHistory } from "react-router-dom";
import "../assets/home-dark.css";

const flagChile = "https://flagcdn.com/w20/cl.png";
const flagUSA = "https://flagcdn.com/w20/us.png";
const flagBrazil = "https://flagcdn.com/w20/br.png";

const RULES = [
  {
    title: { es: "Formato general", en: "General format", pt: "Formato geral" },
    body: {
      es: ["128 jugadores", "Equipos de 8", "Eventos especiales durante el evento"],
      en: ["128 players", "Teams of 8", "Special events during the event"],
      pt: ["128 jogadores", "Equipes de 8", "Eventos especiais durante o evento"]
    }
  },
  {
    title: { es: "Comunicación oficial", en: "Official communication", pt: "Comunicação oficial" },
    body: {
      es: ["La comunicación oficial es por Discord"],
      en: ["Official communication is via Discord"],
      pt: ["Comunicação oficial via Discord"]
    }
  },
  {
    title: { es: "Toxicidad", en: "Toxicity", pt: "Toxicidade" },
    body: {
      es: ["Prohibido racismo/discriminación"],
      en: ["No racism/discrimination"],
      pt: ["Proibido racismo/discriminação"]
    }
  }
];

export default function Reglas() {
  const history = useHistory();
  const [lang, setLang] = React.useState("es");

  React.useEffect(() => {
    window.scrollTo({ top: 0, left: 0, behavior: "smooth" });
  }, []);

  const title =
    lang === "es" ? "Reglamento Rustaco II" : lang === "en" ? "Rustaco II Rules" : "Regras Rustaco II";

  return (
    <div className="page-container">
      <div className="page-hud">
        <div className="hud-left">
          <button className="btn-primary" onClick={() => history.push("/")}>
            Inicio
          </button>
        </div>

        <div className="hud-right">
          <div className="hud-flags">
            <button className="icon-btn flag" onClick={() => setLang("es")} title="Español">
              <img src={flagChile} alt="ES" />
            </button>
            <button className="icon-btn flag" onClick={() => setLang("en")} title="English">
              <img src={flagUSA} alt="EN" />
            </button>
            <button className="icon-btn flag" onClick={() => setLang("pt")} title="Português">
              <img src={flagBrazil} alt="PT" />
            </button>
          </div>
        </div>
      </div>

      <div className="panel">
        <h1 className="reglas-title">{title}</h1>

        <div className="reglas-content">
          {RULES.map((r, idx) => (
            <div key={idx} className="regla-card">
              <div className="regla-header">
                <span className="regla-header-dot" aria-hidden />
                <div className="regla-header-title">{r.title[lang]}</div>
              </div>
              <div className="regla-body">
                <ul>
                  {r.body[lang].map((line, i) => (
                    <li key={i}>{line}</li>
                  ))}
                </ul>
              </div>
            </div>
          ))}
        </div>

        <div className="reglas-cta-row">
          <button className="reglas-cta primary" onClick={() => history.push("/")}>
            Volver
          </button>
        </div>
      </div>
    </div>
  );
}
