import React from "react";
import { Link } from "react-router-dom";
import "../assets/home-dark.css";

// IMPORTA el PDF como asset (Webpack lo convierte en una URL pública)
import reglasPdf from "../assets/docs/reglas-rustaco.pdf";

export default function Reglas() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <header className="TopBar">
        <nav className="TopBar-links" aria-label="Navegación">
          <Link to="/reglas" className="nav-btn">Rules</Link>
          <Link to="/equipos" className="nav-btn">Teams</Link>
          <Link to="/events" className="nav-btn">Stats</Link>
          <div style={{ marginLeft: "auto" }}>
            <Link to="/" className="nav-btn">Home</Link>
          </div>
        </nav>
      </header>

      <main
        style={{
          maxWidth: 900,
          margin: "3rem auto",
          padding: "2rem 1.5rem",
          borderRadius: 16,
          background: "rgba(8,8,12,0.95)",
          boxShadow: "0 18px 60px rgba(0,0,0,0.9)",
          border: "1px solid rgba(255,255,255,0.08)",
          textAlign: "center",
        }}
      >
        <h1 style={{ margin: 0, marginBottom: "1.4rem", fontSize: "2rem", fontWeight: 900 }}>
          Reglas del Evento
        </h1>

        <p style={{ marginBottom: "1.8rem" }}>
          Puedes consultar las reglas completas en formato PDF haciendo clic en el siguiente botón.
        </p>

        <a
          href={reglasPdf}
          target="_blank"
          rel="noopener noreferrer"
          style={{
            display: "inline-flex",
            alignItems: "center",
            justifyContent: "center",
            gap: 8,
            padding: "0.65rem 1.8rem",
            borderRadius: 999,
            background: "linear-gradient(90deg, #e25822 60%, #7289da 100%)",
            color: "#fff",
            fontWeight: 800,
            fontSize: "0.98rem",
            textDecoration: "none",
            boxShadow: "0 6px 18px #000a",
          }}
        >
          Reglas en PDF
        </a>
      </main>
    </div>
  );
}
