import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

import rustgamedark from "../assets/img/rustgamedark.jpg";
import animatedVideo from "../assets/video/animated.mp4";

function VideoBackground() {
  const videoRef = React.useRef(null);
  const [status, setStatus] = React.useState("loading"); // loading | playing | error

  React.useEffect(() => {
    const v = videoRef.current;
    if (!v) return;

    // sin sonido
    v.muted = true;
    v.defaultMuted = true;
    v.volume = 0;

    const tryPlay = () => {
      try {
        const p = v.play();
        if (p && typeof p.catch === "function") p.catch(() => {});
      } catch (_) {}
    };

    tryPlay();
  }, []);

  return (
    <div className="video-bg" aria-hidden="true">
      <video
        ref={videoRef}
        className="video-bg__media"
        poster={rustgamedark}
        autoPlay
        loop
        muted
        playsInline
        preload="auto"
        controls={false}
        onCanPlay={() => setStatus("playing")}
        onError={() => setStatus("error")}
      >
        <source src={animatedVideo} type="video/mp4" />
      </video>

      {/* Debug mínimo para saber si carga (quítalo después) */}
      <div
        style={{
          position: "fixed",
          left: 12,
          bottom: 12,
          zIndex: 50,
          padding: "6px 10px",
          fontSize: 12,
          borderRadius: 10,
          background: "rgba(0,0,0,0.55)",
          border: "1px solid rgba(255,255,255,0.10)",
          color: "#fff"
        }}
      >
        video: {status}
      </div>
    </div>
  );
}

export default function Home() {
  React.useEffect(() => {
    // Activa "solo video" (quita el fondo gradiente global)
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <VideoBackground />

      <main style={{ position: "relative", zIndex: 20, padding: "5rem 1rem" }}>
        <div className="panel" style={{ maxWidth: 900, margin: "0 auto" }}>
          <h1 style={{ marginTop: 0 }}>Rustaco</h1>
          <p className="regla-body">
            Si el texto “video: playing” aparece, el mp4 está cargando.
          </p>
          <div style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
            <Link to="/equipos" className="btn-primary">Equipos</Link>
            <Link to="/reglas" className="btn-primary">Reglas</Link>
          </div>
        </div>
      </main>
    </div>
  );
}
