import React from 'react';
import logo from '../assets/img/logorustaco.png';
import rustpmc1 from '../assets/img/rustpmc1.png';
import rustpmc2 from '../assets/img/rustpmc2.png';
import '../assets/styles.css';

// Barra superior personalizada: logo grande a la izquierda, botones inmediatamente a la derecha del logo
const TopBar = () => (
  <div
    style={{
      width: '100%',
      minHeight: '90px',
      background: 'linear-gradient(90deg, #181818 60%, #e25822 120%)',
      boxShadow: '0 4px 24px #000a',
      display: 'flex',
      alignItems: 'center',
      padding: '0 2.5rem',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      gap: '2.5rem'
    }}
  >
    {/* Logo grande a la izquierda */}
    <div style={{
      display: 'flex',
      alignItems: 'center',
      gap: '1.5rem',
      minWidth: 0
    }}>
      <img
        src={logo}
        alt="Rustaco Logo"
        height={80}
        width={80}
        style={{
          borderRadius: '50%',
          background: 'rgba(35,32,26,0.97)',
          boxShadow: '0 6px 24px #000a, 0 0 0 4px #e25822',
          objectFit: 'cover',
          border: '2.5px solid #23201a',
        }}
      />
      <span style={{
        fontFamily: 'Impact, Arial Black, Arial, sans-serif',
        fontSize: '2.1rem',
        color: 'var(--rust-orange)',
        letterSpacing: '2px',
        textShadow: '2px 2px 8px #000a',
        whiteSpace: 'nowrap'
      }}>
        Rustaco Eventos
      </span>
      {/* Botones alineados inmediatamente a la derecha del logo */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '2.5rem',
        marginLeft: '2.5rem'
      }}>
        <a href="/" className="nav-btn">Inicio</a>
        <a href="/events" className="nav-btn">Stats</a>
        <a href="/about" className="nav-btn">Nosotros</a>
        <a href="/contact" className="nav-btn">Contacto</a>
      </div>
    </div>
  </div>
);

const Header = () => (
  <header className="header" style={{
    marginTop: '0',
    padding: '3rem 0 2.5rem 0', // Menos separación abajo
    textAlign: 'center',
    minHeight: '40vh', // Menos altura
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center'
  }}>
    <h1 style={{
      fontSize: '2.7rem',
      color: 'var(--rust-orange)',
      letterSpacing: '2px',
      marginBottom: '1.2rem',
      textShadow: '2px 2px 8px #000a',
      fontWeight: 900,
      animation: 'fadeDown 1s cubic-bezier(.4,0,.2,1)'
    }}>
      Bienvenido a Rustaco Eventos
    </h1>
    <p style={{
      color: 'var(--rust-accent)',
      fontSize: '1.5rem',
      margin: 0,
      animation: 'fadeDown 1.2s cubic-bezier(.4,0,.2,1)'
    }}>
      Organización profesional de torneos y eventos en el mundo de Rust.
    </p>
  </header>
);

const EventIntro = () => (
  <section
    style={{
      maxWidth: 1300,
      minHeight: '70vh', // Menos altura
      margin: '0 auto',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 2rem',
      textAlign: 'left',
      animation: 'fadeIn 1.2s',
      gap: '0.8rem' // Menos separación entre texto e imagen
    }}
  >
    <div
      style={{
        flex: 1.2,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'center'
      }}
    >
      <h2
        style={{
          color: 'var(--rust-orange)',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          letterSpacing: '1px'
        }}
      >
        ¿De qué trata el evento?
      </h2>
      <p
        style={{
          color: '#f5f5f5',
          fontSize: '1.5rem',
          margin: 0,
          maxWidth: 600
        }}
      >
        Rustaco Eventos organiza competencias y torneos únicos en el universo de Rust, donde equipos y jugadores se enfrentan en desafíos de supervivencia, estrategia y habilidad. ¡Participa para ganar premios, reconocimiento y formar parte de la mejor comunidad de Rust en Latinoamérica!
      </p>
    </div>
    <div
      style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        animation: 'imgSlideIn 1.2s'
      }}
    >
      <img
        src={rustpmc1}
        alt="Evento Rust"
        style={{
          width: '800px',
          maxWidth: '98vw',
          borderRadius: 0,
          boxShadow: 'none',
          objectFit: 'contain',
          marginLeft: '0.5rem',
          marginRight: 0,
          background: 'none',
          animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)'
        }}
      />
    </div>
  </section>
);

const AboutEvent = () => (
  <section
    style={{
      maxWidth: 1300,
      minHeight: '70vh', // Menos altura
      margin: '0 auto',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 2rem',
      textAlign: 'left',
      animation: 'fadeIn 1.4s',
      gap: '0.8rem' // Menos separación entre imagen y texto
    }}
  >
    <div
      style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'flex-end',
        alignItems: 'center',
        animation: 'imgSlideIn 1.2s'
      }}
    >
      <img
        src={rustpmc2}
        alt="Sobre el evento"
        style={{
          width: '800px',
          maxWidth: '98vw',
          borderRadius: 0,
          boxShadow: 'none',
          objectFit: 'contain',
          marginRight: '0.5rem',
          marginLeft: 0,
          background: 'none',
          animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)'
        }}
      />
    </div>
    <div
      style={{
        flex: 1.2,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'center'
      }}
    >
      <h2
        style={{
          color: 'var(--rust-orange)',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          letterSpacing: '1px'
        }}
      >
        Sobre el evento
      </h2>
      <p style={{ color: '#f5f5f5', fontSize: '1.5rem', margin: 0, maxWidth: 700 }}>
        Cada evento está diseñado para ofrecer una experiencia competitiva y divertida, con reglas claras, administración profesional y soporte para todos los participantes. Nos enfocamos en la transparencia, el juego limpio y la integración de nuevos jugadores. ¡Sigue nuestras redes para no perderte el próximo torneo!
      </p>
    </div>
  </section>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <TopBar />
    <Header />
    <EventIntro />
    <AboutEvent />
    <Footer />
  </>
);

export default Home;
