import React, { useState } from 'react';
import '../assets/styles.css';
import '../assets/home-dark.css';
import rustaco2logo from '../assets/img/rustaco2.png';
import rustgamedark from '../assets/img/rustgamedark.jpg';
import steamlogo from '../assets/img/steamlogo.png';

const flagChile = "https://flagcdn.com/w20/cl.png";
const flagUSA = "https://flagcdn.com/w20/us.png";
const flagBrazil = "https://flagcdn.com/w20/br.png";

const translations = {
  es: { formato: 'Formato', sobre: 'Sobre Rustaco', equipos: 'Equipos', verReglas: 'Ver Reglas' },
  en: { formato: 'Format', sobre: 'About Rustaco', equipos: 'Teams', verReglas: 'See Rules' },
  pt: { formato: 'Formato', sobre: 'Sobre o Rustaco', equipos: 'Equipes', verReglas: 'Ver Regras' }
};

function TopBar({ lang, setLang }) {
  return (
    <div className="TopBar" style={{ position: 'sticky', top: 0, zIndex: 200 }}>
      <div style={{ width: '100%', maxWidth: 1200, margin: '0 auto', display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0.8rem 1rem' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <img src={rustaco2logo} alt="Rustaco" style={{ width: 56, height: 56, borderRadius: 8, objectFit: 'cover' }} />
          <nav style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
            <button className="nav-btn">{translations[lang].formato}</button>
            <button className="nav-btn">{translations[lang].sobre}</button>
            <button className="nav-btn">{translations[lang].equipos}</button>
            <a href="/reglas" className="nav-btn">{translations[lang].verReglas}</a>
          </nav>
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: 10 }}>
          <div style={{ display: 'flex', gap: 6 }}>
            <button onClick={() => setLang('es')} title="Español"><img src={flagChile} alt="ES" style={{ width: 26, height: 18 }} /></button>
            <button onClick={() => setLang('en')} title="English"><img src={flagUSA} alt="EN" style={{ width: 26, height: 18 }} /></button>
            <button onClick={() => setLang('pt')} title="Português"><img src={flagBrazil} alt="PT" style={{ width: 26, height: 18 }} /></button>
          </div>
          <a href="https://www.rustaco.site/auth/steam" style={{ display: 'inline-flex', alignItems: 'center', gap: 8, background: 'linear-gradient(90deg,#23201a 60%,#27ae60)', color: '#fff', padding: '0.5rem 0.9rem', borderRadius: 10, textDecoration: 'none', fontWeight: 700 }}>
            <img src={steamlogo} alt="Steam" style={{ width: 20, height: 20 }} /> Login Steam
          </a>
        </div>
      </div>
    </div>
  );
}

function AnimatedBackground() {
  return (
    <div style={{ position: 'fixed', inset: 0, zIndex: 0, pointerEvents: 'none' }}>
      <div style={{ position: 'absolute', inset: 0, backgroundImage: `url(${rustgamedark})`, backgroundSize: 'cover', backgroundPosition: 'center', filter: 'brightness(0.25) contrast(0.95)', opacity: 1 }} />
      <div style={{ position: 'absolute', inset: 0, background: 'linear-gradient(180deg, rgba(0,0,0,0.35), rgba(0,0,0,0.55))' }} />
    </div>
  );
}

export default function Home() {
  const [lang, setLang] = useState('es');

  return (
    <div className="home-dark">
      <AnimatedBackground />
      <TopBar lang={lang} setLang={setLang} />
      <main style={{ position: 'relative', zIndex: 20, paddingTop: 80, paddingBottom: 80, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <img src={rustaco2logo} alt="Rustaco II" style={{ width: 420, height: 420, borderRadius: 12, objectFit: 'contain', boxShadow: '0 30px 90px rgba(0,0,0,0.75)' }} />
      </main>
    </div>
  );
}
