import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

import rustgamedark from "../assets/img/rustgamedark.jpg";
import animatedVideo from "../assets/video/animated.mp4";

function VideoBackground() {
  const videoRef = React.useRef(null);

  React.useEffect(() => {
    const v = videoRef.current;
    if (!v) return;
    v.muted = true;
    v.defaultMuted = true;
    v.volume = 0;
    try {
      const p = v.play();
      if (p && typeof p.catch === "function") p.catch(() => {});
    } catch (_) {}
  }, []);

  return (
    <div className="video-bg" aria-hidden="true">
      <video
        ref={videoRef}
        className="video-bg__media"
        poster={rustgamedark}
        autoPlay
        loop
        muted
        playsInline
        preload="auto"
        controls={false}
      >
        <source src={animatedVideo} type="video/mp4" />
      </video>
      {/* sin overlay */}
    </div>
  );
}

function TopBar() {
  return (
    <div className="TopBar" style={{ position: "sticky", top: 0, zIndex: 200 }}>
      <div
        style={{
          width: "100%",
          maxWidth: 1200,
          margin: "0 auto",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "0.8rem 1rem"
        }}
      >
        <Link to="/" className="nav-btn" style={{ fontWeight: 900 }}>
          Rustaco
        </Link>

        <nav style={{ display: "flex", gap: 12, alignItems: "center" }}>
          <Link to="/equipos" className="nav-btn">Equipos</Link>
          <Link to="/reglas" className="nav-btn">Reglas</Link>
          <Link to="/about" className="nav-btn">Sobre</Link>
        </nav>
      </div>
    </div>
  );
}

export default function Home() {
  return (
    <div className="home-dark">
      <VideoBackground />
      <TopBar />

      <main style={{ position: "relative", zIndex: 20, padding: "6rem 1rem 4rem" }}>
        <section className="panel" style={{ maxWidth: 980, margin: "0 auto" }}>
          <h1 style={{ margin: 0, fontWeight: 900, letterSpacing: 1 }}>Rustaco</h1>
          <p className="regla-body" style={{ marginTop: 10 }}>
            {/* Home simplificado: sin cuenta regresiva, sin aplicaciones, sin fechas, sin banners laterales, sin 3 logos */}
          </p>

          <div style={{ marginTop: 18, display: "flex", gap: 12, flexWrap: "wrap" }}>
            <Link to="/equipos" className="btn-primary">Ver equipos</Link>
            <Link to="/reglas" className="btn-primary" style={{ background: "linear-gradient(90deg,#23201a 60%,#7289da)" }}>
              Ver reglas
            </Link>
          </div>
        </section>
      </main>
    </div>
  );
}
