    discord1: 'Entre no nosso Discord',
    discord2: 'Clique aqui para entrar no servidor',
    footer: 'Rustaco Eventos — Inspirado em Rust. Todos os direitos reservados.',
    equiposLabel: 'Equipes',
    equiposDesc: 'Total de equipes participantes no evento.',
    jugadoresEquipo: 'Jogadores por equipe',
    jugadoresEquipoDesc: 'Cada equipe será composta por 8 jogadores.',
    jugadores: 'Jogadores',
    jugadoresDesc: 'Participantes confirmados para o Rustaco 2.',
    hora: 'Hora',
    horaDesc: 'Horário de início do evento (horário do Chile).',
    modo: 'Modo',
    modoDesc: 'O evento será realizado em formato competitivo.',
    verReglas: 'Ver Regras'
  }
};

// Hook para animaciones al hacer scroll
function useRevealOnScroll(ref, options = {}) {
  useEffect(() => {
    if (!ref?.current) return;
    const node = ref.current;
    const handleIntersect = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          node.classList.add('reveal-visible');
        } else {
          node.classList.remove('reveal-visible');
        }
      });
    };
    const observer = new window.IntersectionObserver(handleIntersect, {
      threshold: options.threshold || 0.15
    });
    observer.observe(node);
    return () => observer.disconnect();
  }, [ref, options.threshold]);
}

const TopBar = ({ onFormatoClick, onInfoClick, onTeamsClick, lang, setLang }) => (
  <div
    style={{
      width: '100%',
      minHeight: '80px',
      background: 'linear-gradient(90deg, #181818 60%, #e25822 120%)',
      boxShadow: '0 2px 12px #0008',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      position: 'sticky',
      top: 0,
      zIndex: 100,
    }}
  >
    <div
      style={{
        width: '100%',
        maxWidth: 1300,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: '0 1.5rem',
      }}
    >
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '2rem',
        minWidth: 0,
      }}>
        <img
          src={logo}
          alt="Rustaco Logo"
          style={{
            borderRadius: '50%',
            width: 64,
            height: 64,
            objectFit: 'cover',
            boxShadow: '0 1px 12px #0007',
          }}
        />
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '2rem',
          marginLeft: '2rem'
        }}>
          <button className="nav-btn" onClick={onFormatoClick}>{translations[lang].formato}</button>
          <button className="nav-btn" onClick={onInfoClick}>{translations[lang].sobre}</button>
          <button className="nav-btn" onClick={onTeamsClick}>{translations[lang].equipos}</button>
          <a href="/events" className="nav-btn">{translations[lang].stats}</a>
          <Link to="/reglas" className="nav-btn">
            {translations[lang].verReglas}
          </Link>
          <LoginSteam />
        </div>
      </div>
      <div
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: 8,
        }}
      >
        {/* ...selector de idioma... */}
        <button
          onClick={() => setLang('es')}
          style={{
            background: lang === 'es' ? '#e25822' : '#23201a',
            border: 'none',
            borderRadius: 6,
            padding: 2,
            cursor: 'pointer',
            marginRight: 2,
            boxShadow: lang === 'es' ? '0 0 0 2px #e25822' : 'none',
            transition: 'box-shadow 0.2s'
          }}
          title="Español LATAM"
        >
          <img src={flagChile} alt="Chile" style={{ width: 26, height: 18, verticalAlign: 'middle', display: 'block' }} />
        </button>
        <button
          onClick={() => setLang('en')}
          style={{
            background: lang === 'en' ? '#3a4bd8' : '#23201a',
            border: 'none',
            borderRadius: 6,
            padding: 2,
            cursor: 'pointer',
            marginRight: 2,
            boxShadow: lang === 'en' ? '0 0 0 2px #3a4bd8' : 'none',
            transition: 'box-shadow 0.2s'
          }}
          title="English USA"
        >
          <img src={flagUSA} alt="USA" style={{ width: 26, height: 18, verticalAlign: 'middle', display: 'block' }} />
        </button>
        <button
          onClick={() => setLang('pt')}
          style={{
            background: lang === 'pt' ? '#27ae60' : '#23201a',
            border: 'none',
            borderRadius: 6,
            padding: 2,
            cursor: 'pointer',
            boxShadow: lang === 'pt' ? '0 0 0 2px #27ae60' : 'none',
            transition: 'box-shadow 0.2s'
          }}
          title="Português Brasil"
        >
          <img src={flagBrazil} alt="Brasil" style={{ width: 26, height: 18, verticalAlign: 'middle', display: 'block' }} />
        </button>
      </div>
    </div>
  </div>
);

// Botón profesional de Login Steam SOLO en la TopBar
const ADMIN_STEAM_IDS = [
  '76561198416933402',
  '76561198067186042',
  '76561199220103836',
  '76561198301561047'
];

const LoginSteam = () => {
  const [user, setUser] = React.useState(null);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    let isMounted = true;
    fetch('https://www.rustaco.site/api/user', {
      credentials: 'include',
      headers: {
        'Cache-Control': 'no-cache',
        'Accept': 'application/json'
      }
    })
      .then(res => {
        if (res.status === 401 || res.status === 403) return { steamid: null };
        return res.json();
      })
      .then(data => {
        if (isMounted) {
          setUser(data && data.steamid ? data : null);
          setLoading(false);
        }
      })
      .catch(() => {
        if (isMounted) setLoading(false);
      });
    return () => { isMounted = false; };
  }, []);

  const handleLogout = (e) => {
    e.preventDefault();
    fetch('https://www.rustaco.site/api/logout', {
      method: 'POST',
      credentials: 'include'
    })
      .then(() => window.location.href = '/')
      .catch(() => window.location.href = '/');
  };

  if (loading) {
    // Muestra un spinner pequeño pero NO bloquea la UI ni overlays
    return (
      <div style={{
        width: 120,
        height: 44,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <span className="loader" style={{
          display: 'inline-block',
          width: 28, height: 28,
          border: '4px solid #e25822',
          borderTop: '4px solid #23201a',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite'
        }} />
        <style>
          {`
            @keyframes spin {
              0% { transform: rotate(0deg);}
              100% { transform: rotate(360deg);}
            }
          `}
        </style>
      </div>
    );
  }

  if (user) {
    return (
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: 10,
        background: '#23201a',
        borderRadius: 10,
        padding: '0.35rem 1.1rem',
        boxShadow: '0 1px 8px #0006',
        color: '#fff',
        fontWeight: 600,
        margin: 0,
        maxWidth: 340,
        height: 44,
        minHeight: 44
      }}>
        <img src={user.avatar} alt="avatar" style={{
          width: 32, height: 32, borderRadius: '50%', marginRight: 6, border: '2px solid #7289da'
        }} />
        <span style={{ color: '#b3cfff', fontWeight: 700, fontSize: '1rem', marginRight: 8 }}>
          {user.name}
        </span>
        {/* Solo muestra el botón Admin Panel si el usuario es admin */}
        {ADMIN_STEAM_IDS.includes(user.steamid) && (
          <a
            href="/admin"
            style={{
              marginLeft: 8,
              color: '#27ae60',
              fontWeight: 700,
              textDecoration: 'underline',
              fontSize: '0.98rem'
            }}
          >
            Admin Panel
          </a>
        )}
        <a
          href="#logout"
          onClick={handleLogout}
          style={{
            marginLeft: 8,
            color: '#e25822',
            fontWeight: 700,
            textDecoration: 'underline',
            fontSize: '0.98rem'
          }}
        >
          Logout
        </a>
      </div>
    );
  }

  // Botón de Login Steam como <a> normal, sin overlays ni eventos extra
  return (
    <a
      href="https://www.rustaco.site/auth/steam"
      style={{
        display: 'inline-flex',
        alignItems: 'center',
        gap: 10,
        background: 'linear-gradient(90deg, #23201a 60%, #27ae60 100%)',
        color: '#fff',
        fontWeight: 700,
        fontSize: '1.08rem',
        padding: '0.55rem 1.5rem 0.55rem 1.1rem',
        border: 'none',
        borderRadius: 10,
        boxShadow: '0 2px 8px #0007',
        cursor: 'pointer',
        letterSpacing: '1px',
        textDecoration: 'none',
        margin: 0,
        height: 44,
        minHeight: 44,
        transition: 'background 0.2s, transform 0.2s, box-shadow 0.2s',
        alignSelf: 'center'
      }}
      title="Iniciar sesión con Steam"
    >
      <img
        src={steamlogo}
        alt="Steam"
        style={{
          width: 28,
          height: 28,
          borderRadius: '50%',
          background: 'transparent',
          marginRight: 8,
          boxShadow: '0 1px 4px #0006',
