const InscripcionBanner = () => {
  const [user, setUser] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const history = useHistory();

  useEffect(() => {
    fetch('/api/user', {
      credentials: 'include',
      headers: {
        'Cache-Control': 'no-cache',
        'Accept': 'application/json'
      }
    })
      .then(res => {
        if (res.status === 401 || res.status === 403) return { steamid: null };
        return res.json();
      })
      .then(data => setUser(data && data.steamid ? data : null))
      .catch(() => setUser(null));
  }, []);

  const handleClick = () => {
    if (user) {
      history.push('/applys');
    } else {
      setShowModal(true);
    }
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      margin: '2.5rem 0 0 0'
    }}>
      <button
        onClick={handleClick}
        style={{
          background: 'linear-gradient(90deg, #27ae60 60%, #e25822 100%)',
          color: '#fff',
          fontWeight: 900,
          fontSize: '1.35rem',
          padding: '1.1rem 2.8rem',
          border: 'none',
          borderRadius: 18,
          boxShadow: '0 4px 18px #27ae6088',
          cursor: 'pointer',
          letterSpacing: '1px',
          textDecoration: 'none',
          marginBottom: '1.2rem',
          opacity: 1,
          transition: 'background 0.2s, transform 0.2s'
        }}
        title="Inscríbete ahora"
      >
        Inscripciones abiertas ¡YA!<br />
        <span style={{ fontWeight: 700, fontSize: '1.08rem', color: '#b3cfff' }}>
          del 6 de Agosto 15:00 GMT-4 hasta el 10 de Septiembre 15:00 GMT-4
        </span>
      </button>
      <span style={{
        color: '#e25822',
        fontSize: '1.18rem',
        fontWeight: 700,
        marginTop: 8,
        opacity: 0.95,
        textAlign: 'center'
      }}>
        ¡No te quedes fuera! Inscribe tu equipo y participa en Rustaco II.
      </span>
      {/* ...el resto del modal y mensaje se puede dejar oculto o eliminar si no se usa */}
    </div>
  );
};

