import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

export default function Home() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <main style={{ position: "relative", zIndex: 20, padding: "5rem 1rem" }}>
        <div className="panel" style={{ maxWidth: 900, margin: "0 auto" }}>
          <h1 style={{ marginTop: 0 }}>Rustaco</h1>
          <p className="regla-body">
            El video se controla desde public/index.html.
          </p>
          <div style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
            <Link to="/equipos" className="btn-primary">Equipos</Link>
            <Link to="/reglas" className="btn-primary">Reglas</Link>
          </div>
        </div>
      </main>
    </div>
  );
}
