import React from "react";
import { Link } from "react-router-dom";
import "../assets/home-dark.css";

// Ruta pública al PDF en /public
const REGLAS_PDF_URL = "/reglas-rustaco.pdf";

export default function Reglas() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <header className="TopBar">
        <nav className="TopBar-links" aria-label="Navegación">
          <Link to="/reglas" className="nav-btn">Rules</Link>
          <Link to="/equipos" className="nav-btn">Teams</Link>
          <Link to="/events" className="nav-btn">Stats</Link>
          <div style={{ marginLeft: "auto" }}>
            <Link to="/" className="nav-btn">Home</Link>
          </div>
        </nav>
      </header>

      <main
        style={{
          maxWidth: 960,
          margin: "3rem auto",
          padding: "2rem 1.5rem",
          borderRadius: 16,
          background: "rgba(8,8,12,0.95)",
          boxShadow: "0 18px 60px rgba(0,0,0,0.9)",
          border: "1px solid rgba(255,255,255,0.08)",
        }}
      >
        <h1 style={{ margin: 0, marginBottom: "1.4rem", fontSize: "2.2rem", fontWeight: 900, textAlign: "center" }}>
          Reglas del Evento
        </h1>

        <div style={{ textAlign: "center", marginBottom: "2rem" }}>
          <p style={{ marginBottom: "1rem" }}>
            Puedes leer el reglamento completo aquí abajo y también abrirlo en PDF usando este botón:
          </p>
          <a
            href={REGLAS_PDF_URL}
            target="_blank"
            rel="noopener noreferrer"
            style={{
              display: "inline-flex",
              alignItems: "center",
              justifyContent: "center",
              gap: 8,
              padding: "0.65rem 1.8rem",
              borderRadius: 999,
              background: "linear-gradient(90deg, #e25822 60%, #7289da 100%)",
              color: "#fff",
              fontWeight: 800,
              fontSize: "0.98rem",
              textDecoration: "none",
              boxShadow: "0 6px 18px #000a",
            }}
          >
            Reglas en PDF
          </a>
        </div>

        <p style={{ opacity: 0.8, fontSize: "0.95rem" }}>
          Aquí va el contenido completo del reglamento en HTML (copiado de tu versión anterior).
        </p>
      </main>
    </div>
  );
}
