// ...otros imports...
import React, { useRef, useEffect } from 'react';
import logo from '../assets/img/logorustaco.png';
import rustpmc1 from '../assets/img/rustpmc1.png';
import rustpmc2 from '../assets/img/rustpmc2.png';
import rustpmc3 from '../assets/img/rustpmc3.png';
import rustpmc4 from '../assets/img/rustpmc4.png';
import logodiscord from '../assets/img/logodiscord.png';
import '../assets/styles.css';

// ...useRevealOnScroll igual...

// TopBar con logo al lado derecho de los botones
const TopBar = ({ onFormatoClick, onInfoClick }) => (
  <div
    style={{
      width: '100%',
      minHeight: '80px',
      background: 'linear-gradient(90deg, #181818 60%, #e25822 120%)',
      boxShadow: '0 2px 12px #0008',
      display: 'flex',
      alignItems: 'center',
      padding: '0 4rem',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      gap: '2rem'
    }}
  >
    <div style={{
      display: 'flex',
      alignItems: 'center',
      gap: '2rem',
      minWidth: 0,
      marginLeft: 0
    }}>
      <button className="nav-btn" onClick={onFormatoClick}>Formato</button>
      <button className="nav-btn" onClick={onInfoClick}>Sobre Rustaco</button>
      <a href="/events" className="nav-btn">Stats</a>
      <img
        src={logo}
        alt="Rustaco Logo"
        style={{
          borderRadius: '50%',
          width: 64,
          height: 64,
          objectFit: 'cover',
          boxShadow: '0 1px 12px #0007',
          marginLeft: '2rem'
        }}
      />
    </div>
  </div>
);

// ...Header, FormatoSection, AboutSection, ExtraInfoSection, DiscordBanner igual...

// Equipos participantes (12 equipos, todos "A confirmar")
const TeamsSection = React.forwardRef((props, ref) => {
  const sectionRef = useRef(null);
  useRevealOnScroll(sectionRef, { threshold: 0.15 });

  const teams = Array.from({ length: 12 }, (_, i) => ({
    name: `Equipo ${i + 1}`,
    status: 'A confirmar'
  }));

  return (
    <section
      ref={sectionRef}
      className="reveal"
      style={{
        maxWidth: 1100,
        margin: '0 auto',
        marginTop: '2.5rem',
        marginBottom: '3.5rem',
        padding: '2.5rem 1.5rem',
        background: 'rgba(24,24,24,0.97)',
        borderRadius: '28px',
        boxShadow: '0 6px 32px #000b',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '2.2rem'
      }}
    >
      <h2 style={{
        fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
        fontWeight: 900,
        color: 'var(--rust-orange)',
        fontSize: '2rem',
        letterSpacing: '2px',
        margin: 0,
        textShadow: '2px 2px 8px #000a'
      }}>
        Equipos participantes
      </h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
          gap: '1.7rem',
          width: '100%',
          marginTop: '1.2rem'
        }}
      >
        {teams.map((team, idx) => (
          <div
            key={team.name}
            className="reveal"
            style={{
              background: 'linear-gradient(135deg, #23201a 70%, #3a4bd8 100%)',
              borderRadius: '16px',
              boxShadow: '0 2px 12px #000a, 0 0 0 1.5px #7289da88',
              padding: '1.5rem 1.2rem',
              minHeight: 110,
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '0.7rem',
              border: 'none',
              fontFamily: 'Montserrat, Arial, sans-serif',
              fontWeight: 700,
              fontSize: '1.08rem',
              color: '#fff',
              letterSpacing: '1px',
              position: 'relative',
              transition: 'transform 0.18s cubic-bezier(.4,0,.2,1), box-shadow 0.18s cubic-bezier(.4,0,.2,1)',
              overflow: 'hidden'
            }}
            onMouseEnter={e => {
              e.currentTarget.style.transform = 'translateY(-5px) scale(1.03)';
              e.currentTarget.style.boxShadow = '0 6px 24px #7289da88, 0 0 0 1.5px #7289da';
            }}
            onMouseLeave={e => {
              e.currentTarget.style.transform = '';
              e.currentTarget.style.boxShadow = '0 2px 12px #000a, 0 0 0 1.5px #7289da88';
            }}
          >
            <span style={{
              fontSize: '1.3rem',
              color: '#b3cfff',
              fontWeight: 900,
              textShadow: '1px 1px 8px #23272a'
            }}>
              {team.name}
            </span>
            <span style={{
              fontSize: '1.08rem',
              color: '#fff',
              fontWeight: 600,
              opacity: 0.85,
              letterSpacing: '1px',
              marginTop: '0.2rem'
            }}>
              {team.status}
            </span>
          </div>
        ))}
      </div>
    </section>
  );
});

// ...EventoInfoSection, Footer igual...

const Home = () => {
  const formatoRef = useRef(null);
  const aboutRef = useRef(null);
  const eventoInfoRef = useRef(null);
  const extraInfoRef = useRef(null);
  const teamsRef = useRef(null);

  const scrollToFormato = () => {
    formatoRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };
  const scrollToAbout = () => {
    aboutRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };

  return (
    <>
      <TopBar onFormatoClick={scrollToFormato} onInfoClick={scrollToAbout} />
      <Header />
      <FormatoSection ref={formatoRef} />
      <AboutSection ref={aboutRef} />
      <ExtraInfoSection ref={extraInfoRef} />
      <EventoInfoSection ref={eventoInfoRef} />
      <TeamsSection ref={teamsRef} />
      <DiscordBanner />
      <Footer />
    </>
  );
};

export default Home;
