// Usa el idioma del Home; elimina el estado local de idioma
const InscripcionBanner = ({ lang }) => {
  const [user, setUser] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const history = useHistory();

  useEffect(() => {
    fetch('/api/user', {
      credentials: 'include',
