        'Cache-Control': 'no-cache',
        'Accept': 'application/json'
      }
    })
      .then(res => {
        if (res.status === 401 || res.status === 403) return { steamid: null };
        return res.json();
      })
      .then(data => setUser(data && data.steamid ? data : null))
      .catch(() => setUser(null));
  }, []);

  const handleClick = () => {
    if (user) {
