            }
            h1, h2, h3 {
              font-size: 1.3rem !important;
              margin-bottom: 1rem !important;
            }
            .evento-info-card.reveal {
              min-width: 120px !important;
              max-width: 99vw !important;
              padding: 0.7rem 0.5rem !important;
              font-size: 0.97rem !important;
            }
            .footer {
              font-size: 0.85rem !important;
              padding: 0.7rem 0.2rem !important;
            }
            .discord-banner {
              right: 4vw !important;
              top: unset !important;
              bottom: 16px !important;
              left: 4vw !important;
              width: 92vw !important;
              border-radius: 12px !important;
              padding: 0.6rem 0.7rem !important;
              font-size: 1rem !important;
            }
            .discord-banner span {
              font-size: 1rem !important;
            }
            .discord-banner img {
              width: 22px !important;
              height: 22px !important;
            }
            .nav-btn {
              font-size: 0.98rem !important;
              padding: 0.5rem 0.7rem !important;
              margin: 0 0.1rem !important;
            }
            .TopBar > div {
              flex-direction: column !important;
              gap: 0.5rem !important;
            }
            .evento-info-card.reveal {
              min-width: 110px !important;
              max-width: 99vw !important;
              padding: 0.7rem 0.5rem !important;
              font-size: 0.93rem !important;
            }
            .reveal section,
            section.reveal {
              gap: 1rem !important;
            }
            .reveal section ul,
            section.reveal ul {
              font-size: 1rem !important;
              padding-left: 1em !important;
            }
            .reveal section p,
            section.reveal p {
              font-size: 1rem !important;
            }
            .reveal section .logo-glow-container,
            section.reveal .logo-glow-container {
              margin-bottom: 1rem !important;
            }
            .TeamsSection .reveal,
            .TeamsSection section.reveal {
              gap: 0.5rem !important;
            }
            .TeamsSection .reveal > div,
            .TeamsSection section.reveal > div {
              grid-template-columns: repeat(2, 1fr) !important;
              gap: 0.7rem !important;
            }
            .TeamsSection .reveal > div > div,
            .TeamsSection section.reveal > div > div {
              padding: 0.7rem 0.4rem !important;
              min-height: 70px !important;
              font-size: 0.93rem !important;
            }
          }
          @media (max-width: 500px) {
            .evento-info-card.reveal {
              min-width: 90px !important;
              font-size: 0.89rem !important;
              padding: 0.5rem 0.2rem !important;
            }
            .footer {
              font-size: 0.85rem !important;
            }
            .discord-banner {
              font-size: 0.95rem !important;
              padding: 0.5rem 0.5rem !important;
            }
            .TeamsSection .reveal > div,
            .TeamsSection section.reveal > div {
              grid-template-columns: 1fr !important;
              gap: 0.5rem !important;
            }
          }
        `}
      </style>
      {/* --- FIN CSS RESPONSIVO --- */}
    </>
  );
};

export default Home;

const MapaSection = React.forwardRef(({ lang }, ref) => {
  const sectionRef = useRef(null);
  useRevealOnScroll(sectionRef, { threshold: 0.15 });

  useEffect(() => {
    if (ref) ref.current = sectionRef.current;
  }, [ref]);

  // Animación: solo flotación horizontal suave, sin desaparecer
  const mapaAnim = {
    initial: { x: 0 },
    animate: {
      x: [0, 18, 0, -18, 0],
      transition: {
        duration: 6,
        ease: "easeInOut",
        repeat: Infinity
      }
    }
  };

  return (
    <motion.section
      ref={sectionRef}
      className="reveal"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
      variants={fadeVariants}
      style={{
        maxWidth: 900,
        margin: '0 auto',
        marginTop: '2.5rem',
        marginBottom: '2.5rem',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '1.5rem',
        background: '#23201a',
        boxShadow: '0 8px 32px #000b',
        borderRadius: 24
      }}
    >
      <h2 style={{
        color: 'var(--rust-orange)',
        fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
        fontWeight: 900,
        fontSize: '2rem',
        marginBottom: '1rem',
        letterSpacing: '1px',
        textAlign: 'center'
      }}>
        Mapa Rustaco II
      </h2>
      <motion.img
        src={mapaRustacooo}
        alt="Mapa Rustaco Evento"
        initial="initial"
        animate="animate"
        variants={mapaAnim}
        style={{
          width: '100%',
          maxWidth: 520,
          height: 'auto',
          borderRadius: 28,
          background: 'none',
          border: '4px solid #e25822',
          marginBottom: 0,
          boxShadow: '0 8px 32px #e2582288'
        }}
      />
      {/* Mensaje de transmisión oficial debajo del mapa */}
      <div style={{
        marginTop: '1.2rem',
        background: 'rgba(34,34,34,0.97)',
        borderRadius: 18,
        padding: '1.1rem 1.2rem',
        boxShadow: '0 2px 12px #0007',
        color: '#fff',
        fontWeight: 700,
        fontSize: '1.12rem',
        display: 'flex',
        alignItems: 'center',
        gap: '12px',
        justifyContent: 'center'
      }}>
        <i className="bi bi-twitch" style={{ fontSize: 28, color: '#9147ff' }}></i>
        <span>
          {translations[lang].streamNotice}&nbsp;
          <a
            href="https://www.twitch.tv/rustacoeventos"
            target="_blank"
            rel="noopener noreferrer"
            style={{
              color: '#9147ff',
              fontWeight: 800,
              textDecoration: 'underline'
            }}
          >
            twitch.tv/rustacoeventos
          </a>
        </span>
      </div>
    </motion.section>
  );
});

