import React, { useRef } from 'react';
import logo from '../assets/img/logorustaco.png';
import rustpmc1 from '../assets/img/rustpmc1.png';
import rustpmc2 from '../assets/img/rustpmc2.png';
import rustpmc3 from '../assets/img/rustpmc3.png';
import rustpmc4 from '../assets/img/rustpmc4.png';
import '../assets/styles.css';

// Animación para el logo del header
const animatedLogoStyle = {
  width: 320,
  height: 320,
  borderRadius: '50%',
  objectFit: 'cover',
  boxShadow: '0 2px 18px #000a',
  margin: '0 auto',
  marginBottom: '1.5rem',
  animation: 'spinLogo 6s linear infinite'
};

// Agrega la animación CSS globalmente
const styleSheet = document.createElement("style");
styleSheet.innerText = `
@keyframes spinLogo {
  0% { transform: rotate(0deg) scale(1);}
  50% { transform: rotate(180deg) scale(1.08);}
  100% { transform: rotate(360deg) scale(1);}
}
`;
document.head.appendChild(styleSheet);

const TopBar = ({ onFormatoClick, onInfoClick }) => (
  <div
    style={{
      width: '100%',
      minHeight: '80px',
      background: 'linear-gradient(90deg, #181818 60%, #e25822 120%)',
      boxShadow: '0 2px 12px #0008',
      display: 'flex',
      alignItems: 'center',
      padding: '0 4rem',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      gap: '2rem'
    }}
  >
    <div style={{
      display: 'flex',
      alignItems: 'center',
      gap: '2.5rem',
      minWidth: 0,
      marginLeft: '2rem'
    }}>
      <img
        src={logo}
        alt="Rustaco Logo"
        style={{
          borderRadius: '50%',
          width: 64,
          height: 64,
          objectFit: 'cover',
          boxShadow: '0 1px 12px #0007'
        }}
      />
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '2rem',
        marginLeft: '2rem'
      }}>
        <button className="nav-btn" onClick={onFormatoClick}>Formato</button>
        <button className="nav-btn" onClick={onInfoClick}>Sobre Rustaco</button>
        <a href="/events" className="nav-btn">Stats</a>
      </div>
    </div>
  </div>
);

const Header = () => (
  <header className="header" style={{
    marginTop: '0',
    padding: '3rem 0 1.2rem 0',
    textAlign: 'center',
    minHeight: '30vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center'
  }}>
    <div className="logo-glow-container">
      <img
        src={logo}
        alt="Rustaco Logo"
        style={{
          width: 320,
          height: 320,
          borderRadius: '50%',
          objectFit: 'cover',
          boxShadow: '0 2px 18px #000a',
          margin: '0 auto',
          marginBottom: '1.5rem',
          position: 'relative',
          zIndex: 2
        }}
      />
      <div className="logo-glow-bg"></div>
    </div>
  </header>
);

const FormatoSection = React.forwardRef((props, ref) => (
  <section
    ref={ref}
    style={{
      maxWidth: 1600,
      minHeight: '60vh',
      margin: '0 auto',
      marginTop: '26rem',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 2rem',
      textAlign: 'left',
      animation: 'fadeIn 1.2s',
      gap: '1.5rem',
      boxSizing: 'border-box',
      width: '100%'
    }}
  >
    <div
      style={{
        flex: 1.2,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'center',
        minWidth: 0
      }}
    >
      <h2
        style={{
          fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
          fontWeight: 800,
          color: 'var(--rust-orange)',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          letterSpacing: '1px'
        }}
      >
        Formato - Rustaco II
      </h2>
      <ul style={{
        color: '#f5f5f5',
        fontFamily: 'Montserrat, Arial, sans-serif',
        fontWeight: 500,
        fontSize: '1.5rem',
        margin: 0,
        maxWidth: 650,
        paddingLeft: '1.2em',
        listStyle: 'disc'
      }}>
        <li>
          Rustaco II contará con <b>96 streamers</b> participando en el evento.
        </li>
        <li>
          12 equipos formados por 8 jugadores lucharán por el primer puesto.
        </li>
        <li>
          Cada equipo tendrá su propia isla donde podrán construir y equiparse estratégicamente para el enfrentamiento final.
        </li>
      </ul>
    </div>
    <div
      style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        animation: 'imgSlideIn 1.2s',
        minWidth: 0
      }}
    >
      <img
        src={rustpmc4}
        alt="Evento Rust"
        style={{
          width: '100%',
          maxWidth: '1000px', // Más grande
          minWidth: '400px',
          borderRadius: 0,
          boxShadow: 'none',
          objectFit: 'contain',
          marginLeft: '0.5rem',
          marginRight: 0,
          background: 'none',
          animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)',
          display: 'block'
        }}
      />
    </div>
  </section>
));

const AboutSection = React.forwardRef((props, ref) => (
  <section
    ref={ref}
    style={{
      maxWidth: 1600,
      minHeight: '60vh',
      margin: '0 auto',
      marginTop: '2.5rem',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 2rem',
      textAlign: 'left',
      animation: 'fadeIn 1.2s',
      gap: '1.5rem',
      boxSizing: 'border-box',
      width: '100%',
      background: 'transparent'
    }}
  >
    <div
      style={{
        flex: 1.3,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minWidth: 0
      }}
    >
      <img
        src={rustpmc3}
        alt="Sobre Rustaco"
        style={{
          width: '100%',
          maxWidth: '900px', // Más grande
          minWidth: '400px',
          borderRadius: 0,
          boxShadow: 'none',
          objectFit: 'contain',
          background: 'transparent',
          animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)',
          display: 'block'
        }}
      />
    </div>
    <div
      style={{
        flex: 1.1,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'flex-start',
        minWidth: 0
      }}
    >
      <h2
        style={{
          fontFamily: 'Montserrat, Impact, Arial Black, Arial, sans-serif',
          fontWeight: 900,
          color: 'var(--rust-orange)',
          marginBottom: '2rem',
          fontSize: '2.7rem',
          letterSpacing: '1px',
          textAlign: 'left'
        }}
      >
        Sobre Rustaco
      </h2>
      <p style={{
        color: '#f5f5f5',
        fontFamily: 'Montserrat, Arial, sans-serif',
        fontWeight: 500,
        fontSize: '1.5rem',
        margin: 0,
        maxWidth: 700,
        textAlign: 'left'
      }}>
        Rustaco fue formado por un grupo de amigos con experiencia brutal en Rust, que han decidido crear torneos y eventos competitivos para la comunidad LATAM.
      </p>
    </div>
  </section>
));

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => {
  const formatoRef = useRef(null);
  const aboutRef = useRef(null);

  const scrollToFormato = () => {
    formatoRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };
  const scrollToAbout = () => {
    aboutRef.current?.scrollIntoView({ behavior: 'smooth', block: 'start' });
  };

  return (
    <>
      <TopBar onFormatoClick={scrollToFormato} onInfoClick={scrollToAbout} />
      <Header />
      <FormatoSection ref={formatoRef} />
      <AboutSection ref={aboutRef} />
      <Footer />
    </>
  );
};

export default Home;
