import React from "react";
import { Link } from "react-router-dom";
import "../assets/home-dark.css";

// Ruta al PDF servido desde /public (asegúrate de tener public/reglas-rustaco.pdf)
const REGLAS_PDF_URL = "/reglas-rustaco.pdf";

export default function Reglas() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <header className="TopBar">
        <nav className="TopBar-links" aria-label="Navegación">
          <Link to="/reglas" className="nav-btn">
            Rules
          </Link>
          <Link to="/equipos" className="nav-btn">
            Teams
          </Link>
          <Link to="/events" className="nav-btn">
            Stats
          </Link>
          <div style={{ marginLeft: "auto" }}>
            <Link to="/" className="nav-btn">
              Home
            </Link>
          </div>
        </nav>
      </header>

      <main className="reglas-page">
        <h1 className="reglas-title">Reglas del Evento</h1>

        <p className="reglas-intro">
          Aquí podrás consultar todas las reglas del evento. También puedes abrirlas en un PDF
          separado usando el siguiente botón.
        </p>

        <div style={{ textAlign: "center", marginBottom: "1.8rem" }}>
          <a
            href={REGLAS_PDF_URL}
            target="_blank"
            rel="noopener noreferrer"
            className="reglas-pdf-link"
          >
            Reglas en PDF
          </a>
        </div>
      </main>
    </div>
  );
}
