import React from 'react';
import logo from '../assets/img/logorustaco.png';
import rustpmc1 from '../assets/img/rustpmc1.png';
import rustpmc2 from '../assets/img/rustpmc2.png';
import '../assets/styles.css';

// Barra superior personalizada: logo profesional a la izquierda, botones a la derecha del logo, sin lÃ­nea abajo
const TopBar = () => (
  <div
    style={{
      width: '100%',
      minHeight: '80px',
      background: 'linear-gradient(90deg, #181818 60%, #e25822 120%)',
      boxShadow: '0 2px 12px #0008',
      display: 'flex',
      alignItems: 'center',
      padding: '0 2.5rem',
      position: 'sticky',
      top: 0,
      zIndex: 100,
      gap: '2rem'
    }}
  >
    {/* Logo profesional a la izquierda, mÃ¡s compacto y decorado */}
    <div style={{
      display: 'flex',
      alignItems: 'center',
      gap: '1.2rem',
      minWidth: 0
    }}>
      <div style={{
        background: 'radial-gradient(circle at 60% 40%, #e25822 0%, #23201a 80%)',
        borderRadius: '50%',
        boxShadow: '0 2px 8px #000a, 0 0 0 2px #e25822',
        padding: '4px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        border: '1.5px solid #23201a',
        width: 54,
        height: 54,
        position: 'relative'
      }}>
        <img
          src={logo}
          alt="Rustaco Logo"
          style={{
            borderRadius: '50%',
            width: 46,
            height: 46,
            objectFit: 'cover',
            boxShadow: '0 1px 6px #0007'
          }}
        />
        {/* DecoraciÃ³n extra: brillo */}
        <span style={{
          position: 'absolute',
          top: 6,
          left: 12,
          width: 18,
          height: 8,
          background: 'rgba(255,255,255,0.18)',
          borderRadius: '50%',
          transform: 'rotate(-18deg)'
        }} />
      </div>
      <span style={{
        fontFamily: 'Impact, Arial Black, Arial, sans-serif',
        fontSize: '1.7rem',
        color: 'var(--rust-orange)',
        letterSpacing: '2px',
        textShadow: '2px 2px 8px #000a',
        whiteSpace: 'nowrap'
      }}>
        Rustaco Eventos
      </span>
      {/* Botones alineados inmediatamente a la derecha del logo */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '2rem',
        marginLeft: '2rem'
      }}>
        <a href="/" className="nav-btn">Inicio</a>
        <a href="/events" className="nav-btn">Stats</a>
        <a href="/about" className="nav-btn">Nosotros</a>
        <a href="/contact" className="nav-btn">Contacto</a>
      </div>
    </div>
  </div>
);

const Header = () => (
  <header className="header" style={{
    marginTop: '0',
    padding: '3rem 0 2.5rem 0',
    textAlign: 'center',
    minHeight: '40vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center'
  }}>
    <h1 style={{
      fontSize: '2.7rem',
      color: 'var(--rust-orange)',
      letterSpacing: '2px',
      marginBottom: '1.2rem',
      textShadow: '2px 2px 8px #000a',
      fontWeight: 900,
      animation: 'fadeDown 1s cubic-bezier(.4,0,.2,1)'
    }}>
      Bienvenido a Rustaco Eventos
    </h1>
    <p style={{
      color: 'var(--rust-accent)',
      fontSize: '1.5rem',
      margin: 0,
      animation: 'fadeDown 1.2s cubic-bezier(.4,0,.2,1)'
    }}>
      OrganizaciÃ³n profesional de torneos y eventos en el mundo de Rust.
    </p>
  </header>
);

const EventIntro = () => (
  <section
    style={{
      maxWidth: 1300,
      minHeight: '70vh',
      margin: '0 auto',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 2rem',
      textAlign: 'left',
      animation: 'fadeIn 1.2s',
      gap: '0.8rem'
    }}
  >
    <div
      style={{
        flex: 1.2,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'center'
      }}
    >
      <h2
        style={{
          color: 'var(--rust-orange)',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          letterSpacing: '1px'
        }}
      >
        Â¿De quÃ© trata el evento?
      </h2>
      <p
        style={{
          color: '#f5f5f5',
          fontSize: '1.5rem',
          margin: 0,
          maxWidth: 600
        }}
      >
        Rustaco Eventos organiza competencias y torneos Ãºnicos en el universo de Rust, donde equipos y jugadores se enfrentan en desafÃ­os de supervivencia, estrategia y habilidad. Â¡Participa para ganar premios, reconocimiento y formar parte de la mejor comunidad de Rust en LatinoamÃ©rica!
      </p>
    </div>
    <div
      style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'flex-start',
        alignItems: 'center',
        animation: 'imgSlideIn 1.2s'
      }}
    >
      <img
        src={rustpmc1}
        alt="Evento Rust"
        style={{
          width: '800px',
          maxWidth: '98vw',
          borderRadius: 0,
          boxShadow: '0 0 32px #0006',
          objectFit: 'contain',
          marginLeft: '0.5rem',
          marginRight: 0,
          background: 'none',
          animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)',
          border: '3px solid #23201a',
          outline: '2px solid #e25822',
          outlineOffset: '-8px'
        }}
      />
    </div>
  </section>
);

const AboutEvent = () => (
  <section
    style={{
      maxWidth: 1300,
      minHeight: '70vh',
      margin: '0 auto',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '0 2rem',
      textAlign: 'left',
      animation: 'fadeIn 1.4s',
      gap: '0.8rem'
    }}
  >
    <div
      style={{
        flex: 1,
        display: 'flex',
        justifyContent: 'flex-end',
        alignItems: 'center',
        animation: 'imgSlideIn 1.2s'
      }}
    >
      <img
        src={rustpmc2}
        alt="Sobre el evento"
        style={{
          width: '800px',
          maxWidth: '98vw',
          borderRadius: 0,
          boxShadow: '0 0 32px #0006',
          objectFit: 'contain',
          marginRight: '0.5rem',
          marginLeft: 0,
          background: 'none',
          animation: 'imgPop 1.2s cubic-bezier(.4,0,.2,1)',
          border: '3px solid #23201a',
          outline: '2px solid #e25822',
          outlineOffset: '-8px'
        }}
      />
    </div>
    <div
      style={{
        flex: 1.2,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        justifyContent: 'center'
      }}
    >
      <h2
        style={{
          color: 'var(--rust-orange)',
          marginBottom: '2rem',
          fontSize: '2.5rem',
          letterSpacing: '1px'
        }}
      >
        Sobre el evento
      </h2>
      <p style={{ color: '#f5f5f5', fontSize: '1.5rem', margin: 0, maxWidth: 700 }}>
        Cada evento estÃ¡ diseÃ±ado para ofrecer una experiencia competitiva y divertida, con reglas claras, administraciÃ³n profesional y soporte para todos los participantes. Nos enfocamos en la transparencia, el juego limpio y la integraciÃ³n de nuevos jugadores. Â¡Sigue nuestras redes para no perderte el prÃ³ximo torneo!
      </p>
    </div>
  </section>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <TopBar />
    <Header />
    <EventIntro />
    <AboutEvent />
    <Footer />
  </>
);

export default Home;
