import React from 'react';
import logo from '../assets/img/logorustaco.png';
import '../assets/styles.css';

// Navbar con logo grande centrado y los botones alrededor del logo
const Navbar = () => (
  <nav className="navbar navbar-centered navbar-thin" style={{ position: 'relative', minHeight: '120px', padding: 0 }}>
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        width: '100%',
        gap: '2.5rem',
        position: 'relative',
        paddingTop: '1.5rem',
        paddingBottom: '1.5rem'
      }}
    >
      <a href="/" className="nav-btn">Inicio</a>
      <a href="/events" className="nav-btn">Eventos</a>
      <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <img
          src={logo}
          alt="Rustaco Logo"
          className="logo-animated logo-large"
          height={100}
          width={100}
          style={{
            borderRadius: '50%',
            background: 'rgba(35,32,26,0.97)',
            boxShadow: '0 8px 32px #000a, 0 0 0 5px #e25822',
            margin: '0 2.5rem',
            zIndex: 10,
          }}
        />
      </span>
      <a href="/about" className="nav-btn">Nosotros</a>
      <a href="/contact" className="nav-btn">Contacto</a>
    </div>
  </nav>
);

const Header = () => (
  <header className="header" style={{ marginTop: '2.5rem' }}>
    <h1>Bienvenido a Rustaco Eventos</h1>
    <p>Organización profesional de torneos y actividades inspiradas en el universo Rust</p>
  </header>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <Navbar />
    <Header />
    <main>
      <section style={{ textAlign: 'center', margin: '3rem 0' }}>
        <h2 style={{ color: 'var(--rust-orange)', fontSize: '2rem' }}>¡Descubre la experiencia Rustaco!</h2>
        <p style={{ fontSize: '1.2rem', color: 'var(--rust-accent)' }}>
          Participa en torneos, talleres y encuentros únicos para la comunidad Rust.<br />
          Explora nuestras secciones para más información.
        </p>
      </section>
    </main>
    <Footer />
  </>
);

export default Home;
