const Reglas = () => {
  const history = useHistory();
  const [lang, setLang] = useState('es');

  let reglas;
  if (lang === 'en') reglas = reglasEn;
  else if (lang === 'pt') reglas = reglasPt;
  else reglas = reglasEs;

  return (
    <div
      style={{
        maxWidth: 1050,
        margin: '3rem auto',
        background: 'rgba(24,24,24,0.98)',
        borderRadius: 32,
        boxShadow: '0 16px 64px #000c',
        padding: '3.2rem 2.5rem 2.5rem 2.5rem',
        color: '#fff',
        fontFamily: 'Montserrat, Arial, sans-serif',
        border: '2px solid #e2582244',
        position: 'relative',
        overflow: 'hidden'
      }}
    >
      <SideAnimation side="left" />
      <SideAnimation side="right" />

      {/* Selector de idioma */}
      <div style={{ position: 'absolute', top: 24, right: 32, zIndex: 10 }}>
        <label style={{ color: '#b3cfff', fontWeight: 600, marginRight: 8 }}>
          {translations[lang].idioma}:
        </label>
        <select
          value={lang}
          onChange={e => setLang(e.target.value)}
          style={{
            background: '#23201a',
            color: '#fff',
            border: '1.5px solid #7289da',
            borderRadius: 8,
            padding: '4px 12px',
            fontWeight: 600,
            fontSize: '1rem'
          }}
        >
          <option value="es">Español</option>
          <option value="en">English</option>
          <option value="pt">Português</option>
        </select>
      </div>

      <h1
        style={{
          color: 'var(--rust-orange)',
          fontWeight: 900,
          fontSize: '2.8rem',
          marginBottom: '2.5rem',
          textAlign: 'center',
          letterSpacing: '2px',
          textShadow: '0 2px 18px #000a',
          zIndex: 2,
          position: 'relative',
          animation: 'fadeInDown 1s cubic-bezier(.39,.575,.565,1) both'
        }}
      >
        {translations[lang].titulo}
      </h1>
      <div>
        {reglas.map((regla, idx) => (
          <div
            key={idx}
            style={{
              ...reglaCardStyle,
              ...fadeInDelay(0.1 * idx)
            }}
            className="regla-card"
          >
            <div
              style={{
                fontWeight: 800,
                color: '#f39c12',
                fontSize: '1.22rem',
                marginBottom: 8,
                letterSpacing: '1px',
                textShadow: '0 1px 8px #000a'
              }}
            >
              {regla.titulo}
            </div>
            <div style={{ fontSize: '1.09rem', color: '#e0e0e0', lineHeight: 1.7 }}>
              {regla.descripcion}
            </div>
          </div>
        ))}
      </div>
      <div
        style={{
          marginTop: '2.5rem',
          padding: '1.2rem',
          background: 'linear-gradient(90deg, #181818 80%, #23201a 100%)',
          borderRadius: 16,
          color: '#b3cfff',
          fontSize: '1.12rem',
          textAlign: 'center',
          boxShadow: '0 2px 12px #0007',
          border: '1.5px solid #7289da44',
          animation: 'fadeInUp 1.2s cubic-bezier(.39,.575,.565,1) both'
        }}
      >
        <b>Nota:</b> {translations[lang].nota}
        <br />
        {translations[lang].dudas}{' '}
        <a
          href="https://discord.gg/rustaco"
          target="_blank"
          rel="noopener noreferrer"
          style={{ color: '#7289da', textDecoration: 'underline', fontWeight: 600 }}
        >
          {translations[lang].discord}
        </a>
        .
      </div>
      <div
        style={{
          marginTop: '2.5rem',
          display: 'flex',
          justifyContent: 'center',
          gap: 16,
          animation: 'fadeInUp 1.4s cubic-bezier(.39,.575,.565,1) both'
        }}
      >
        <button
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          style={{
            background: 'linear-gradient(90deg, #e25822 60%, #7289da 100%)',
            color: '#fff',
            fontWeight: 700,
            fontSize: '1.15rem',
            padding: '0.9rem 2.5rem',
            border: 'none',
            borderRadius: 14,
            boxShadow: '0 2px 12px #0007',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.2s',
            letterSpacing: '1px'
          }}
          onMouseOver={e => (e.currentTarget.style.transform = 'scale(1.07)')}
          onMouseOut={e => (e.currentTarget.style.transform = 'scale(1)')}
        >
          {translations[lang].volverArriba}
        </button>
        <button
          onClick={() => history.push('/')}
          style={{
            background: 'linear-gradient(90deg, #7289da 60%, #e25822 100%)',
            color: '#fff',
            fontWeight: 700,
            fontSize: '1.15rem',
            padding: '0.9rem 2.5rem',
            border: 'none',
            borderRadius: 14,
            boxShadow: '0 2px 12px #0007',
            cursor: 'pointer',
            transition: 'background 0.2s, transform 0.2s',
            letterSpacing: '1px'
          }}
          onMouseOver={e => (e.currentTarget.style.transform = 'scale(1.07)')}
          onMouseOut={e => (e.currentTarget.style.transform = 'scale(1)')}
        >
          {translations[lang].irInicio}
        </button>
      </div>
      <style>
        {`
          @keyframes fadeInUp {
            0% {
              opacity: 0;
              transform: translate3d(0, 40px, 0);
            }
            100% {
              opacity: 1;
              transform: none;
            }
          }
          @keyframes fadeInDown {
            0% {
              opacity: 0;
              transform: translate3d(0, -40px, 0);
            }
            100% {
              opacity: 1;
              transform: none;
            }
          }
          .regla-card:hover {
            box-shadow: 0 12px 36px #000c, 0 0 0 0 #0000;
            transform: translateY(-4px) scale(1.015);
          }
          .regla-card img {
            transition: transform 0.3s cubic-bezier(.39,.575,.565,1), box-shadow 0.3s cubic-bezier(.39,.575,.565,1);
          }
          .regla-card img:hover {
            transform: scale(1.18) rotate(-6deg);
            box-shadow: 0 4px 24px #7289da88;
          }
          .regla-card:hover img {
            transform: scale(1.15) rotate(-3deg);
            box-shadow: 0 2px 16px #0007;
          }
          .regla-card:hover {
            box-shadow: 0 12px 36px #000c, 0 0 0 0 #0000;
            transform: translateY(-4px) scale(1.015);
          }
          .regla-card {
            transition: box-shadow 0.2s, transform 0.2s;
          }
          .regla-card:hover {
            box-shadow: 0 12px 36px #000c, 0 0 0 0 #0000;
            transform: translateY(-4px) scale(1.015);
          }
          .regla-card img {
            transition: transform 0.3s cubic-bezier(.39,.575,.565,1), box-shadow 0.3s cubic-bezier(.39,.575,.565,1);
          }
          .regla-card img:hover {
            transform: scale(1.18) rotate(-6deg);
            box-shadow: 0 4px 24px #7289da88;
          }
          .regla-card:hover img {
            transform: scale(1.15) rotate(-3deg);
            box-shadow: 0 2px 16px #0007;
          }
          @keyframes sideAnimation {
            0% { box-shadow: 0 0 0 0 #0000; }
            50% { box-shadow: 0 0 32px 12px #7289dacc, 0 0 0 0 #0000; }
            100% { box-shadow: 0 0 32px 12px #7289dacc, 0 0 0 0 #0000; }
          }
          .side-animation {
            position: absolute;
            top: 0;
            bottom: 0;
            width: 50px;
            z-index: 1;
            animation: sideAnimation 2s infinite alternate;
          }
          .side-animation.left {
            left: 0;
            background: linear-gradient(90deg, #7289da88, transparent);
          }
          .side-animation.right {
            right: 0;
            background: linear-gradient(90deg, transparent, #7289da88);
          }
        `}
      </style>
    </div>
  );
}

export default Reglas;
