import React from 'react';
import logo from '../assets/img/logorustaco.png';
import '../assets/styles.css';

// Navbar más delgada, logo sobresaliente hacia abajo y botones a los lados del logo
const Navbar = () => (
  <nav className="navbar navbar-centered navbar-thin" style={{ position: 'relative', minHeight: '60px', paddingTop: '0.5rem', paddingBottom: '0.5rem' }}>
    <div className="navbar-links navbar-links-left" style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
      <a href="/">Inicio</a>
      <a href="/events">Eventos</a>
    </div>
    <div
      style={{
        position: 'relative',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flex: 1,
        minHeight: 0,
      }}
    >
      <img
        src={logo}
        alt="Rustaco Logo"
        className="logo-animated logo-large"
        height={90}
        width={90}
        style={{
          borderRadius: '50%',
          background: 'rgba(35,32,26,0.97)',
          boxShadow: '0 8px 32px #000a, 0 0 0 5px #e25822',
          marginTop: '10px',
          marginBottom: '-35px',
          zIndex: 10,
        }}
      />
    </div>
    <div className="navbar-links navbar-links-right" style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
      <a href="/about">Nosotros</a>
      <a href="/contact">Contacto</a>
    </div>
  </nav>
);

const Header = () => (
  <header className="header" style={{ marginTop: '3.5rem' }}>
    <h1>Bienvenido a Rustaco Eventos</h1>
    <p>Organización profesional de torneos y actividades inspiradas en el universo Rust</p>
  </header>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <Navbar />
    <Header />
    <main>
      <section style={{ textAlign: 'center', margin: '3rem 0' }}>
        <h2 style={{ color: 'var(--rust-orange)', fontSize: '2rem' }}>¡Descubre la experiencia Rustaco!</h2>
        <p style={{ fontSize: '1.2rem', color: 'var(--rust-accent)' }}>
          Participa en torneos, talleres y encuentros únicos para la comunidad Rust.<br />
          Explora nuestras secciones para más información.
        </p>
      </section>
    </main>
    <Footer />
  </>
);

export default Home;
