import React from 'react';

// IMPORTA TUS IMÁGENES (ajusta las rutas si es necesario)
import eraPrimitivo from '../assets/img/primitivo.png';
import eraTier1 from '../assets/img/tier1.png';
import eraTier2 from '../assets/img/tier2.png';
import eraTier3 from '../assets/img/tier3.png';
import eraRaids from '../assets/img/raids.png';

const reglas = [
  {
    titulo: "1. Formato general",
    descripcion: (
      <ul style={{ margin: 0, paddingLeft: '1.5em', listStyle: 'disc' }}>
        <li><b>112 jugadores</b> en total.</li>
        <li><b>Equipos de 8 participantes</b>.</li>
        <li>Se realizarán <b>diversos eventos especiales</b> durante el evento.</li>
        <li><b>Fishing Bandits</b>.</li>
      </ul>
    )
  },
  {
    titulo: "2. Mecánica principal: Sistema de Dogtags",
    descripcion: (
      <span>
        Cada jugador, al ser eliminado, soltará un <b>dogtag</b> (placa identificatoria). Los equipos deberán recolectar los dogtags de sus rivales y depositarlos en el <b>armario principal</b> de su base para que sean contabilizados. <br />
        <span style={{ color: "#f39c12" }}>Solo los dogtags depositados correctamente sumarán puntos para el equipo.</span>
      </span>
    )
  },
  {
    titulo: "3. Eliminación por destrucción del TC",
    descripcion: (
      <span>
        Durante la <b>era de raideos</b>, si el <b>TC (armario principal)</b> de tu equipo es destruido, <span style={{ color: "#e25822", fontWeight: 700 }}>tu equipo quedará eliminado</span> del evento.
      </span>
    )
  },
  {
    titulo: "4. Estructura y reglas de las islas y bases",
    descripcion: (
      <ul style={{ margin: 0, paddingLeft: '1.5em', listStyle: 'disc' }}>
        <li>
          Cada isla cuenta con:
          <ul style={{ margin: 0, paddingLeft: '1.5em', listStyle: 'circle' }}>
            <li>Un espacio designado para la <b>base principal (main)</b>.</li>
            <li>Un área específica para la <b>zona de huerto</b> (6x7).</li>
            <li>Una <b>entrada a metro</b> exclusiva para cada isla.</li>
          </ul>
        </li>
        <li>
          La <b>base main</b> debe construirse únicamente en el espacio asignado (14x14). <br />
          <span style={{ color: "#f39c12" }}>No está permitido sobrepasar los límites de la zona asignada, incluyendo la zona de huerto.</span>
        </li>
        <li>
          El <b>TC (armario principal)</b> de la base main debe ser accesible en todo momento. Solo puede estar protegido por puertas o ventanas, <b>no puede estar completamente cerrado ni grifeado</b>.
        </li>
        <li>
          El <b>TC</b> puede ser movido en caso de ser necesario, pero <b>debe solicitarse el cambio a la administración</b>. <br />
          <span style={{ color: "#f39c12" }}>El TC main solo podrá ser cambiado hasta 1 hora antes del inicio de la era de raideos.</span>
        </li>
      </ul>
    )
  },
  {
    titulo: "5. Respeto y deportividad",
    descripcion: "Todos los participantes deben mantener una actitud respetuosa y deportiva hacia otros jugadores, organizadores y espectadores. Cualquier conducta tóxica, insultos o discriminación resultará en sanciones o expulsión."
  },
  {
    titulo: "6. Prohibido el uso de trampas",
    descripcion: "Está estrictamente prohibido el uso de hacks, cheats, exploits o cualquier software externo que otorgue ventajas injustas. El incumplimiento será motivo de descalificación inmediata."
  },
  {
    titulo: "7. Cumplimiento de horarios",
    descripcion: "Los equipos deben presentarse y estar listos en los horarios establecidos por la organización. Retrasos injustificados pueden resultar en penalizaciones."
  },
  {
    titulo: "8. Comunicación oficial",
    descripcion: "Toda comunicación oficial se realizará a través del servidor de Discord de Rustaco. Es responsabilidad de los capitanes estar atentos a los anuncios y notificaciones."
  },
  {
    titulo: "9. Decisiones de la organización",
    descripcion: "Las decisiones tomadas por la organización son finales e inapelables. Se recomienda leer atentamente todas las reglas y consultar dudas antes del evento."
  }
];

const eras = [
  {
    nombre: "Era Primitivo",
    imagen: eraPrimitivo,
    descripcion: "Comienzo del evento con armas y herramientas primitivas."
  },
  {
    nombre: "Era Tier 1",
    imagen: eraTier1,
    descripcion: "Acceso a equipamiento y armas de Tier 1."
  },
  {
    nombre: "Era Tier 2",
    imagen: eraTier2,
    descripcion: "Se habilitan armas y objetos de Tier 2."
  },
  {
    nombre: "Era Tier 3",
    imagen: eraTier3,
    descripcion: "Desbloqueo de todo el arsenal y equipamiento de Tier 3."
  },
  {
    nombre: "Era Raids",
    imagen: eraRaids,
    descripcion: "Comienza la era de raideos, se habilitan explosivos y ataques a bases."
  }
];

const reglaCardStyle = {
  background: 'linear-gradient(120deg, #23201a 80%, #2d2d2d 100%)',
  borderRadius: '14px',
  boxShadow: '0 2px 16px #000a',
  padding: '1.3rem 1.5rem',
  marginBottom: '1.5rem',
  borderLeft: '5px solid var(--rust-orange)',
  transition: 'box-shadow 0.2s',
};

const eraCardStyle = {
  background: 'linear-gradient(120deg, #23201a 80%, #3a4bd8 100%)',
  borderRadius: '14px',
  boxShadow: '0 2px 16px #000a',
  padding: '1.2rem 1.2rem',
  marginBottom: '1.2rem',
  display: 'flex',
  alignItems: 'center',
  gap: '1.5rem',
  borderLeft: '5px solid #7289da',
};

const Reglas = () => (
  <div style={{
    maxWidth: 900,
    margin: '3rem auto',
    background: 'rgba(24,24,24,0.98)',
    borderRadius: 22,
    boxShadow: '0 8px 32px #000c',
    padding: '2.7rem 2.2rem',
    color: '#fff',
    fontFamily: 'Montserrat, Arial, sans-serif',
    border: '1.5px solid #e2582233'
  }}>
    <h1 style={{
      color: 'var(--rust-orange)',
      fontWeight: 900,
      fontSize: '2.5rem',
      marginBottom: '2.2rem',
      textAlign: 'center',
      letterSpacing: '2px',
      textShadow: '0 2px 12px #000a'
    }}>
      Reglas del Evento
    </h1>

    {/* Apartado de Eras */}
    <div style={{ marginBottom: '2.5rem' }}>
      <h2 style={{
        color: '#7289da',
        fontWeight: 800,
        fontSize: '1.6rem',
        marginBottom: '1.2rem',
        textAlign: 'center',
        letterSpacing: '1px'
      }}>
        Evento por Eras
      </h2>
      <div>
        {eras.map((era, idx) => (
          <div key={idx} style={eraCardStyle}>
            <img
              src={era.imagen}
              alt={era.nombre}
              style={{
                width: 70,
                height: 70,
                objectFit: 'contain',
                borderRadius: 12,
                background: '#181818',
                boxShadow: '0 1px 8px #0007'
              }}
            />
            <div>
              <div style={{ fontWeight: 700, fontSize: '1.18rem', color: '#b3cfff', marginBottom: 4 }}>
                {era.nombre}
              </div>
              <div style={{ fontSize: '1.05rem', color: '#e0e0e0' }}>
                {era.descripcion}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>

    {/* Reglas */}
    <div>
      {reglas.map((regla, idx) => (
        <div key={idx} style={reglaCardStyle}>
          <div style={{
            fontWeight: 800,
            color: '#f39c12',
            fontSize: '1.18rem',
            marginBottom: 6,
            letterSpacing: '1px'
          }}>
            {regla.titulo}
          </div>
          <div style={{ fontSize: '1.08rem', color: '#e0e0e0', lineHeight: 1.7 }}>
            {regla.descripcion}
          </div>
        </div>
      ))}
    </div>
    <div style={{
      marginTop: '2.5rem',
      padding: '1.2rem',
      background: 'linear-gradient(90deg, #181818 80%, #23201a 100%)',
      borderRadius: 14,
      color: '#b3cfff',
      fontSize: '1.08rem',
      textAlign: 'center',
      boxShadow: '0 2px 12px #0007',
      border: '1px solid #7289da44'
    }}>
      <b>Nota:</b> El incumplimiento de estas reglas puede resultar en sanciones, expulsión del evento o baneo permanente de futuras competencias.
      <br />
      Para dudas o consultas, contacta a la organización en nuestro <a href="https://discord.gg/rustaco" target="_blank" rel="noopener noreferrer" style={{ color: '#7289da', textDecoration: 'underline' }}>Discord oficial</a>.
    </div>
  </div>
);

export default Reglas;
