// Hook para animaciones al hacer scroll
function useRevealOnScroll(ref, options = {}) {
  useEffect(() => {
    if (!ref?.current) return;
    const node = ref.current;
    const handleIntersect = (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          node.classList.add('reveal-visible');
        } else {
          node.classList.remove('reveal-visible');
        }
      });
    };
    const observer = new window.IntersectionObserver(handleIntersect, {
      threshold: options.threshold || 0.15
    });
    observer.observe(node);
    return () => observer.disconnect();
  }, [ref, options.threshold]);
}

const Header = () => (
  <header style={{ textAlign: 'center', padding: '2rem 0' }}>
    <img src={logo} alt="Rustaco Logo" style={{ width: 180, borderRadius: '50%', boxShadow: '0 2px 18px #000a' }} />
    <h1 style={{ color: '#fff', marginTop: '1.5rem', fontWeight: 900, fontSize: '2.5rem' }}>Rustaco II</h1>
  </header>
);

const FormatoSection = React.forwardRef(({ lang }, ref) => (
  <section ref={ref} style={{ maxWidth: 900, margin: '2rem auto', color: '#fff', background: '#23201a', borderRadius: 16, padding: '2rem', boxShadow: '0 2px 12px #000a' }}>
    <h2 style={{ color: 'var(--rust-orange)', fontWeight: 800 }}>{translations[lang].formatoTitulo}</h2>
    <ul style={{ fontSize: '1.2rem', marginTop: 16 }}>
      <li dangerouslySetInnerHTML={{ __html: translations[lang].formato1 }} />
      <li>{translations[lang].formato2}</li>
      <li>{translations[lang].formato3}</li>
    </ul>
    <img src={rustpmc4} alt="Evento Rust" style={{ width: '100%', maxWidth: 400, margin: '2rem auto 0', display: 'block', borderRadius: 12 }} />
  </section>
));

const AboutSection = React.forwardRef(({ lang }, ref) => (
  <section ref={ref} style={{ maxWidth: 900, margin: '2rem auto', color: '#fff', background: '#23201a', borderRadius: 16, padding: '2rem', boxShadow: '0 2px 12px #000a' }}>
    <h2 style={{ color: 'var(--rust-orange)', fontWeight: 800 }}>{translations[lang].sobreTitulo}</h2>
    <p style={{ fontSize: '1.2rem', marginTop: 16 }}>{translations[lang].sobreTexto}</p>
    <img src={rustpmc3} alt="Sobre Rustaco" style={{ width: '100%', maxWidth: 400, margin: '2rem auto 0', display: 'block', borderRadius: 12 }} />
  </section>
));

const ExtraInfoSection = React.forwardRef(({ lang }, ref) => (
  <section ref={ref} style={{ maxWidth: 700, margin: '2rem auto', color: '#fff', background: '#181818', borderRadius: 16, padding: '2rem', boxShadow: '0 2px 12px #000a' }}>
    <h3 style={{ color: 'var(--rust-orange)', fontWeight: 700 }}>{translations[lang].infoAdicional}</h3>
    <p>{translations[lang].infoAdicionalTexto}</p>
  </section>
));

const EventoInfoSection = React.forwardRef(({ lang }, ref) => (
  <section ref={ref} style={{ maxWidth: 900, margin: '2rem auto', color: '#fff', background: '#23201a', borderRadius: 16, padding: '2rem', boxShadow: '0 2px 12px #000a' }}>
    <h2 style={{ color: 'var(--rust-orange)', fontWeight: 800 }}>{translations[lang].eventoTitulo}</h2>
    <ul style={{ fontSize: '1.1rem', marginTop: 16 }}>
      <li>{translations[lang].equiposLabel}: 14</li>
      <li>{translations[lang].jugadoresEquipo}: 8</li>
      <li>{translations[lang].jugadores}: 112</li>
      <li>{translations[lang].hora}: 17:00 (GMT-4)</li>
      <li>{translations[lang].fecha}: {translations[lang].fechaDesc}</li>
      <li>{translations[lang].modo}: {translations[lang].modoDesc}</li>
    </ul>
  </section>
));

const TeamsSection = React.forwardRef(({ lang }, ref) => {
  const teams = Array.from({ length: 14 }, (_, i) => ({
    name: `${translations[lang].equipo} ${i + 1}`,
    status: translations[lang].aConfirmar
  }));

  return (
    <section ref={ref} style={{ maxWidth: 1100, margin: '2rem auto', color: '#fff' }}>
      <h2 style={{ color: 'var(--rust-orange)', fontWeight: 800, textAlign: 'center' }}>{translations[lang].equiposParticipantes}</h2>
      <p style={{ textAlign: 'center', opacity: 0.85 }}>{translations[lang].equiposTexto}</p>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
        gap: '1.7rem',
        width: '100%',
        marginTop: '1.2rem'
      }}>
        {teams.map((team, idx) => (
          <div key={team.name} style={{
            background: 'linear-gradient(135deg, #23201a 70%, #3a4bd8 100%)',
            borderRadius: '16px',
            boxShadow: '0 2px 12px #000a, 0 0 0 1.5px #7289da88',
            padding: '1.5rem 1.2rem',
            minHeight: 110,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            gap: '0.7rem',
            border: 'none',
            fontFamily: 'Montserrat, Arial, sans-serif',
            fontWeight: 700,
            fontSize: '1.08rem',
            color: '#fff',
            letterSpacing: '1px',
            position: 'relative',
            transition: 'transform 0.18s cubic-bezier(.4,0,.2,1), box-shadow 0.18s cubic-bezier(.4,0,.2,1)',
            overflow: 'hidden'
          }}>
            <span style={{
              fontSize: '1.3rem',
              color: '#b3cfff',
              fontWeight: 900,
              textShadow: '1px 1px 8px #23272a'
            }}>
              {team.name}
            </span>
            <span style={{
              fontSize: '1.08rem',
              color: '#fff',
              fontWeight: 600,
              opacity: 0.85,
              letterSpacing: '1px',
              marginTop: '0.2rem'
            }}>
              {team.status}
            </span>
          </div>
        ))}
      </div>
    </section>
  );
});

