        {translations[lang].discord2}
      </span>
    </span>
  </a>
);

const Footer = ({ lang }) => (
  <footer className="footer" style={{ color: '#fff', textAlign: 'center', padding: '2rem 0', marginTop: '2rem' }}>
    &copy; {new Date().getFullYear()} {translations[lang].footer}
