import React from 'react';
import logo from '../assets/img/logorustaco.png';
import '../assets/styles.css';

// Navbar ultra delgada, logo grande y sobresaliente hacia abajo, todo bien alineado
const Navbar = () => (
  <nav
    className="navbar navbar-centered navbar-thin"
    style={{
      position: 'relative',
      minHeight: '32px',
      padding: 0,
      background: 'linear-gradient(90deg, #23201a 80%, #e25822 100%)',
      borderBottom: '2px solid var(--rust-orange)',
      boxShadow: 'var(--rust-shadow)',
      zIndex: 2,
    }}
  >
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'flex-start',
        width: '100%',
        gap: '2.5rem',
        position: 'relative',
        paddingTop: '0.2rem',
        paddingBottom: '0.2rem',
      }}
    >
      <a href="/" className="nav-btn">Inicio</a>
      <a href="/events" className="nav-btn">Eventos</a>
      <span className="logo-navbar-wrapper">
        <img
          src={logo}
          alt="Rustaco Logo"
          className="logo-animated logo-navbar-overlap"
          height={120}
          width={120}
        />
      </span>
      <a href="/about" className="nav-btn">Nosotros</a>
      <a href="/contact" className="nav-btn">Contacto</a>
    </div>
  </nav>
);

const Header = () => (
  <header className="header" style={{ marginTop: '2.8rem', padding: '0.7rem 0 0.3rem 0' }}>
    <h1 style={{
      fontSize: '2rem',
      color: 'var(--rust-orange)',
      letterSpacing: '2px',
      marginBottom: '0.3rem',
      textShadow: '2px 2px 8px #000a',
      fontWeight: 900,
    }}>
      Bienvenido a Rustaco Eventos
    </h1>
    <p style={{ color: 'var(--rust-accent)', fontSize: '1.1rem', margin: 0 }}>
      Organización profesional de torneos y actividades inspiradas en el universo Rust
    </p>
  </header>
);

const Footer = () => (
  <footer className="footer">
    &copy; {new Date().getFullYear()} Rustaco Eventos &mdash; Inspirado en Rust. Todos los derechos reservados.
  </footer>
);

const Home = () => (
  <>
    <Navbar />
    <Header />
    <main style={{
      maxWidth: 900,
      margin: '2rem auto',
      padding: '2rem',
      background: 'rgba(35,32,26,0.95)',
      borderRadius: 'var(--rust-radius)',
      boxShadow: 'var(--rust-shadow)',
      animation: 'fadeIn 1.2s',
      minHeight: '350px',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center'
    }}>
      <section style={{ textAlign: 'center', margin: '1.5rem 0' }}>
        <h2 style={{
          color: 'var(--rust-orange)',
          fontSize: '1.5rem',
          marginBottom: '1rem',
          letterSpacing: '1px',
          fontWeight: 700,
        }}>
          ¡Descubre la experiencia Rustaco!
        </h2>
        <p style={{ fontSize: '1.1rem', color: 'var(--rust-accent)', margin: 0 }}>
          Participa en torneos, talleres y encuentros únicos para la comunidad Rust.<br />
          Explora nuestras secciones para más información.
        </p>
      </section>
    </main>
    <Footer />
  </>
);

export default Home;
