import React from "react";
import { Link } from "react-router-dom";
import "../assets/styles.css";
import "../assets/home-dark.css";

import rustgamedark from "../assets/img/rustgamedark.jpg";
import bundledVideo from "../assets/video/animated.mp4"; // fallback (si /public/video no existe)

function VideoBackground() {
  const videoRef = React.useRef(null);

  const publicVideo = `${process.env.PUBLIC_URL}/video/animated.mp4`;
  const sources = [publicVideo, bundledVideo];

  const [srcIdx, setSrcIdx] = React.useState(0);
  const [status, setStatus] = React.useState("loading"); // loading | ready | error
  const [ready, setReady] = React.useState(false);

  const src = sources[srcIdx];

  React.useEffect(() => {
    const v = videoRef.current;
    if (!v) return;

    setReady(false);
    setStatus("loading");

    // fuerza reload cuando cambia la fuente
    try {
      v.load();
    } catch (_) {}

    // autoplay sin sonido
    v.muted = true;
    v.defaultMuted = true;
    v.volume = 0;

    try {
      const p = v.play();
      if (p && typeof p.catch === "function") p.catch(() => {});
    } catch (_) {}
  }, [src]);

  const markReady = () => {
    setReady(true);
    setStatus("ready");
  };

  const onError = () => {
    // fallback: si falla /public/video/animated.mp4, usa el import desde src/assets/video
    if (srcIdx < sources.length - 1) {
      setSrcIdx((i) => i + 1);
      return;
    }
    setStatus("error");
  };

  return (
    <div className="video-bg" aria-hidden="true">
      {/* Poster mientras carga (temporal) */}
      <div className="video-bg__poster" style={{ backgroundImage: `url(${rustgamedark})` }} />

      <video
        ref={videoRef}
        className="video-bg__media"
        autoPlay
        muted
        loop
        playsInline
        preload="auto"
        controls={false}
        onLoadedData={markReady}
        onCanPlayThrough={markReady}
        onError={onError}
        style={{ opacity: ready ? 1 : 0, transition: "opacity 240ms ease" }}
      >
        <source type="video/mp4" src={src} />
      </video>

      {/* Debug mínimo (quitar después) */}
      <div
        style={{
          position: "fixed",
          left: 12,
          bottom: 12,
          zIndex: 50,
          padding: "6px 10px",
          fontSize: 12,
          borderRadius: 10,
          background: "rgba(0,0,0,0.55)",
          border: "1px solid rgba(255,255,255,0.10)",
          color: "#fff"
        }}
      >
        video: {status} | src: {srcIdx === 0 ? "/public" : "bundled"}
      </div>
    </div>
  );
}

export default function Home() {
  React.useEffect(() => {
    document.body.classList.add("has-video-bg");
    return () => document.body.classList.remove("has-video-bg");
  }, []);

  return (
    <div className="home-dark has-video-bg">
      <VideoBackground />

      <main style={{ position: "relative", zIndex: 20, padding: "5rem 1rem" }}>
        <div className="panel" style={{ maxWidth: 900, margin: "0 auto" }}>
          <h1 style={{ marginTop: 0 }}>Rustaco</h1>
          <p className="regla-body">
            El video aparece solo cuando está listo (ready).
          </p>
          <div style={{ display: "flex", gap: 10, flexWrap: "wrap" }}>
            <Link to="/equipos" className="btn-primary">Equipos</Link>
            <Link to="/reglas" className="btn-primary">Reglas</Link>
          </div>
        </div>
      </main>
    </div>
  );
}
